server/elevenlabs.ts.content:
import WebSocket from 'ws';
import { EventEmitter } from 'events';
import { queries } from './database';

const ELEVENLABS_API_KEY = process.env.ELEVENLABS_API_KEY;
const ELEVENLABS_WS_URL = 'wss://api.elevenlabs.io/v1/text-to-speech';

interface ElevenLabsSession {
  ws: WebSocket | null;
  voiceId: string;
  model: string;
  isConnected: boolean;
  lastActivity: number;
}

class ElevenLabsService extends EventEmitter {
  private sessions = new Map<string, ElevenLabsSession>();
  private latencyMetrics: number[] = [];

  constructor() {
    super();
    this.setupCleanupInterval();
  }

  // Get voice configuration from database
  private getVoiceConfig(voiceType: 'masc' | 'fem') {
    const voiceIdKey = voiceType === 'masc' ? 'VOZ_MASC_ID' : 'VOZ_FEM_ID';
    const modelKey = 'MODELO';

    const voiceIdResult = queries.getSetting.get(voiceIdKey) as { value: string } | undefined;
    const modelResult = queries.getSetting.get(modelKey) as { value: string } | undefined;

    const voiceId = voiceIdResult?.value || 'pNInz6obpgDQGcFmaJgB';
    const model = modelResult?.value || 'eleven_multilingual_v2'; // Modelo mais natural

    return { voiceId, model };
  }

  // Start TTS session
  async startTTSSession(sessionId: string, voiceType: 'masc' | 'fem'): Promise<boolean> {
    try {
      const { voiceId, model } = this.getVoiceConfig(voiceType);

      console.log(`[ELEVENLABS] Starting TTS session ${sessionId} with voice ${voiceId}`);

      const ws = new WebSocket(`${ELEVENLABS_WS_URL}/${voiceId}/stream-input?model_id=${model}`, {
        headers: {
          'xi-api-key': ELEVENLABS_API_KEY
        }
      });

      const session: ElevenLabsSession = {
        ws,
        voiceId,
        model,
        isConnected: false,
        lastActivity: Date.now()
      };

      ws.on('open', () => {
        console.log(`[ELEVENLABS] TTS session ${sessionId} connected`);
        session.isConnected = true;
        session.lastActivity = Date.now();

        // Configuração inicial otimizada para naturalidade
        ws.send(JSON.stringify({
          text: " ",
          voice_settings: {
            stability: 0.35,        // Mais emotivo e natural
            similarity_boost: 0.95, // Máxima similaridade
            style: 0.15,           // Estilo sutil para naturalidade  
            use_speaker_boost: true // Para clareza máxima
          },
          generation_config: {
            chunk_length_schedule: [120, 160, 250, 290]
          },
          // Configurações adicionais para reduzir robotização
          optimize_streaming_latency: 0, // Prioriza qualidade sobre velocidade
          output_format: "pcm_22050"     // Formato otimizado para streaming
        }));

        this.emit('tts-session-ready', sessionId);
      });

      ws.on('message', (data) => {
        session.lastActivity = Date.now();
        const startTime = Date.now();

        try {
          const message = JSON.parse(data.toString());

          if (message.audio) {
            // Calculate latency
            const latency = Date.now() - startTime;
            this.recordLatency(latency);

            // Emit audio data for Twilio
            this.emit('tts-audio', sessionId, Buffer.from(message.audio, 'base64'));
          }

          if (message.isFinal) {
            console.log(`[ELEVENLABS] TTS final chunk for session ${sessionId}`);
          }
        } catch (error) {
          console.error(`[ELEVENLABS] Error parsing message:`, error);
        }
      });

      ws.on('error', (error) => {
        console.error(`[ELEVENLABS] TTS session ${sessionId} error:`, error);
        session.isConnected = false;
        this.emit('tts-session-error', sessionId, error);
      });

      ws.on('close', () => {
        console.log(`[ELEVENLABS] TTS session ${sessionId} closed`);
        session.isConnected = false;
        this.sessions.delete(sessionId);
        this.emit('tts-session-closed', sessionId);
      });

      this.sessions.set(sessionId, session);
      return true;

    } catch (error) {
      console.error(`[ELEVENLABS] Failed to start TTS session:`, error);
      return false;
    }
  }

  // Send text to TTS
  sendTTSText(sessionId: string, text: string, style?: string): boolean {
    const session = this.sessions.get(sessionId);

    if (!session || !session.isConnected || !session.ws) {
      console.warn(`[ELEVENLABS] TTS session ${sessionId} not available`);
      return false;
    }

    try {
      const voiceSettings: any = {
        stability: 0.5,
        similarity_boost: 0.8,
        use_speaker_boost: true
      };

      // Apply style if provided
      if (style) {
        switch (style) {
          case 'formal':
            voiceSettings.stability = 0.7;
            voiceSettings.style = 0.2;
            break;
          case 'simpatico':
            voiceSettings.stability = 0.3;
            voiceSettings.style = 0.8;
            break;
          default: // neutro
            voiceSettings.style = 0.0;
        }
      }

      session.ws.send(JSON.stringify({
        text: text,
        voice_settings: voiceSettings
      }));

      session.lastActivity = Date.now();
      return true;

    } catch (error) {
      console.error(`[ELEVENLABS] Error sending TTS text:`, error);
      return false;
    }
  }

  // Start STT session for captions
  async startSTTSession(): Promise<WebSocket | null> {
    try {
      console.log('[ELEVENLABS] Starting STT session for captions');

      const sttWs = new WebSocket('wss://api.elevenlabs.io/v1/speech-to-text/stream', {
        headers: {
          'xi-api-key': ELEVENLABS_API_KEY
        }
      });

      sttWs.on('open', () => {
        console.log('[ELEVENLABS] STT session connected');

        // Send configuration
        sttWs.send(JSON.stringify({
          language: 'pt',
          model: 'whisper-large-v3'
        }));
      });

      sttWs.on('message', (data) => {
        try {
          const result = JSON.parse(data.toString());

          if (result.text) {
            this.emit('stt-transcript', {
              text: result.text,
              isFinal: result.is_final || false
            });
          }
        } catch (error) {
          console.error('[ELEVENLABS] STT parse error:', error);
        }
      });

      sttWs.on('error', (error) => {
        console.error('[ELEVENLABS] STT error:', error);
      });

      return sttWs;

    } catch (error) {
      console.error('[ELEVENLABS] Failed to start STT session:', error);
      return null;
    }
  }

  // Stop TTS session
  stopTTSSession(sessionId: string): boolean {
    const session = this.sessions.get(sessionId);

    if (session && session.ws) {
      console.log(`[ELEVENLABS] Stopping TTS session ${sessionId}`);
      session.ws.close();
      this.sessions.delete(sessionId);
      return true;
    }
    return false;
  }

  // Get available voices from ElevenLabs
  async getVoices(): Promise<any[]> {
    try {
      const response = await fetch('https://api.elevenlabs.io/v1/voices', {
        headers: {
          'xi-api-key': ELEVENLABS_API_KEY!
        }
      });

      if (!response.ok) {
        throw new Error(`ElevenLabs API error: ${response.status}`);
      }

      const data = await response.json();
      return data.voices || [];

    } catch (error) {
      console.error('[ELEVENLABS] Failed to fetch voices:', error);
      return [];
    }
  }

  // Record latency metrics
  private recordLatency(latency: number): void {
    this.latencyMetrics.push(latency);

    // Keep only last 10 measurements
    if (this.latencyMetrics.length > 10) {
      this.latencyMetrics.shift();
    }
  }

  // Get average latency
  getAverageLatency(): number {
    if (this.latencyMetrics.length === 0) return 0;

    const sum = this.latencyMetrics.reduce((a, b) => a + b, 0);
    return Math.round(sum / this.latencyMetrics.length);
  }

  // Cleanup inactive sessions
  private setupCleanupInterval(): void {
    setInterval(() => {
      const now = Date.now();
      const timeout = 300000; // 5 minutes

      for (const [sessionId, session] of Array.from(this.sessions.entries())) {
        if (now - session.lastActivity > timeout) {
          console.log(`[ELEVENLABS] Cleaning up inactive session: ${sessionId}`);
          this.stopTTSSession(sessionId);
        }
      }
    }, 60000); // Check every minute
  }
}

export const elevenLabsService = new ElevenLabsService();

server/healthCheck.ts.content:
// Health check endpoint para verificar se as variáveis estão disponíveis no ambiente público
import { Request, Response } from 'express';

export function setupHealthCheck(app: any) {
  // Endpoint para verificar se as chaves estão configuradas
  app.get('/api/health', (req: Request, res: Response) => {
    const requiredEnvVars = [
      'ELEVENLABS_API_KEY',
      'TWILIO_ACCOUNT_SID', 
      'TWILIO_AUTH_TOKEN',
      'TWILIO_NUMBER',
      'DEEPGRAM_API_KEY'
    ];

    const envStatus: { [key: string]: boolean } = {};
    const missingVars: string[] = [];

    requiredEnvVars.forEach(envVar => {
      const isPresent = !!process.env[envVar];
      envStatus[envVar] = isPresent;

      if (!isPresent) {
        missingVars.push(envVar);
      }
    });

    const allPresent = missingVars.length === 0;

    res.json({
      status: allPresent ? 'healthy' : 'unhealthy',
      timestamp: new Date().toISOString(),
      environment: process.env.NODE_ENV || 'development',
      replit_domain: process.env.REPLIT_DEV_DOMAIN,
      missing_variables: missingVars,
      variables_status: envStatus,
      message: allPresent 
        ? 'Todas as variáveis estão configuradas' 
        : `${missingVars.length} variáveis faltando: ${missingVars.join(', ')}`
    });
  });

  // Endpoint específico para validar chaves da API
  app.post('/api/validate-keys', async (req: Request, res: Response) => {
    const results: { [key: string]: { valid: boolean; error?: string } } = {};

    // Teste ElevenLabs
    try {
      const elevenlabsKey = process.env.ELEVENLABS_API_KEY;
      if (!elevenlabsKey) {
        results.elevenlabs = { valid: false, error: 'API key not found' };
      } else {
        const response = await fetch('https://api.elevenlabs.io/v1/user', {
          headers: { 'xi-api-key': elevenlabsKey }
        });
        results.elevenlabs = { 
          valid: response.ok,
          error: response.ok ? undefined : `HTTP ${response.status}: ${response.statusText}`
        };
      }
    } catch (error) {
      results.elevenlabs = { 
        valid: false, 
        error: error instanceof Error ? error.message : 'Unknown error' 
      };
    }

    // Teste Twilio
    try {
      const twilioSid = process.env.TWILIO_ACCOUNT_SID;
      const twilioToken = process.env.TWILIO_AUTH_TOKEN;

      if (!twilioSid || !twilioToken) {
        results.twilio = { valid: false, error: 'Credentials not found' };
      } else {
        const auth = Buffer.from(`${twilioSid}:${twilioToken}`).toString('base64');
        const response = await fetch(`https://api.twilio.com/2010-04-01/Accounts/${twilioSid}.json`, {
          headers: { 'Authorization': `Basic ${auth}` }
        });
        results.twilio = { 
          valid: response.ok,
          error: response.ok ? undefined : `HTTP ${response.status}: ${response.statusText}`
        };
      }
    } catch (error) {
      results.twilio = { 
        valid: false, 
        error: error instanceof Error ? error.message : 'Unknown error' 
      };
    }

    // Teste Deepgram
    try {
      const deepgramKey = process.env.DEEPGRAM_API_KEY;
      if (!deepgramKey) {
        results.deepgram = { valid: false, error: 'API key not found' };
      } else {
        const response = await fetch('https://api.deepgram.com/v1/projects', {
          headers: { 'Authorization': `Token ${deepgramKey}` }
        });
        results.deepgram = { 
          valid: response.ok,
          error: response.ok ? undefined : `HTTP ${response.status}: ${response.statusText}`
        };
      }
    } catch (error) {
      results.deepgram = { 
        valid: false, 
        error: error instanceof Error ? error.message : 'Unknown error' 
      };
    }

    const allValid = Object.values(results).every(result => result.valid);

    res.json({
      status: allValid ? 'all_valid' : 'some_invalid',
      timestamp: new Date().toISOString(),
      results,
      summary: {
        total: Object.keys(results).length,
        valid: Object.values(results).filter(r => r.valid).length,
        invalid: Object.values(results).filter(r => !r.valid).length
      }
    });
  });
}

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import http from "http";
import { WebSocketServer } from "ws";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  // Create HTTP server
  const server = http.createServer(app);

  // Setup WebSocket for /media (resolve 31920)
  const wss = new WebSocketServer({ noServer: true });

  server.on('upgrade', (req, socket, head) => {
    if (req.url === '/media') {
      wss.handleUpgrade(req, socket, head, ws => wss.emit('connection', ws, req));
    } else {
      socket.destroy();
    }
  });

  wss.on('connection', (ws) => {
    console.log('[media] conectado');
    ws.on('message', (msg) => {
      try {
        const data = JSON.parse(msg.toString());
        if (data.event === 'start') console.log('[media] start', data.start?.callSid || 'unknown');
        if (data.event === 'media') {
          // Audio base64 data in data.media.payload
          // Log occasionally to avoid spam
          if (Math.random() < 0.01) console.log('[media] receiving audio');
        }
        if (data.event === 'stop') console.log('[media] stop');
      } catch(e) { 
        console.error('[media] parse error:', e instanceof Error ? e.message : 'unknown'); 
      }
    });

    ws.on('close', () => console.log('[media] desconectado'));

    // Keep-alive
    const ping = setInterval(() => {
      try { ws.ping(); } catch(e) {}
    }, 15000);
    ws.on('close', () => clearInterval(ping));
  });

  await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // Start server
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`HTTP/WS on ${port}`);
  });
})();

