          keyInfo: `Key starts with: ${elevenlabsKey.substring(0, 3)}..., length: ${elevenlabsKey.length}`
        };
      } else {
        const response = await fetch('https://api.elevenlabs.io/v1/user', {
          headers: { 'xi-api-key': elevenlabsKey }
        });

        if (response.ok) {
