        return res.status(400).json({ error: "callSid obrigatório" });
      }

      console.log(`[TWILIO] Starting real recording for call: ${callSid}`);

      // Start recording via Twilio API
      const twilioClient = twilio(TWILIO_ACCOUNT_SID, TWILIO_AUTH_TOKEN);
      const recording = await twilioClient.calls(callSid).recordings.create({
        recordingTrack: 'both',
        trim: 'do-not-trim',
        recordingStatusCallback: `${process.env.REPL_SLUG ? `https://${process.env.REPL_SLUG}.${process.env.REPLIT_DEV_DOMAIN}` : 'http://localhost:5000'}/api/recordings/twilio/callback`
      });

      const timestamp = Date.now();
      const filename = `${phoneNumber || 'unknown'}-${timestamp}.mp3`;

      queries.addRecording.run(callSid, phoneNumber || 'unknown', filename);
      const recordingDb = queries.getLastRecording.get() as any;

      console.log(`[TWILIO] Real recording started: ${recording.sid}`);
      res.json({ 
        success: true, 
        id: recordingDb.id, 
        recordingSid: recording.sid,
        filename, 
        callSid 
      });
    } catch (error) {
      console.error('[TWILIO] Error starting real recording:', error);
      res.status(500).json({ error: error instanceof Error ? error.message : String(error) });
    }
  });

  // Twilio recording callback - downloads the MP3 file
  app.post("/api/recordings/twilio/callback", async (req, res) => {
    try {
      const { RecordingSid, RecordingUrl, RecordingDuration, CallSid } = req.body;

      console.log(`[TWILIO] Recording completed: ${RecordingSid}, Duration: ${RecordingDuration}s`);

      // Download the recording file from Twilio
      const response = await fetch(RecordingUrl);
      if (!response.ok) throw new Error('Failed to download recording');

      const buffer = await response.arrayBuffer();
      const recordingsDir = path.join(process.cwd(), 'recordings');
      await fs.mkdir(recordingsDir, { recursive: true });

      // Find the recording in database by CallSid
      const recording = queries.getRecordingByCallSid.get(CallSid) as any;
      if (recording) {
        const filePath = path.join(recordingsDir, recording.filename);
        await fs.writeFile(filePath, Buffer.from(buffer));

        // Update database with final duration and size
        queries.updateRecording.run(parseInt(RecordingDuration), buffer.byteLength, recording.id);

        console.log(`[TWILIO] Recording saved: ${recording.filename} (${buffer.byteLength} bytes)`);
      }

      res.send('<Response></Response>'); // TwiML response
    } catch (error) {
      console.error('[TWILIO] Error processing recording callback:', error);
      res.status(500).send('<Response></Response>');
    }
  });

  // TwiML endpoint for Twilio calls - CRÍTICO para funcionar
  app.get("/twiml", (req, res) => {
    const { voiceType } = req.query;
    console.log(`[TWIML] Serving TwiML for voice type: ${voiceType}`);

    res.type('text/xml');
    res.send(`<?xml version="1.0" encoding="UTF-8"?>
<Response>
  <Say voice="alice" language="pt-BR">Olá! Você está conectado ao sistema Abmix. Esta chamada está sendo controlada pelo painel de discagem inteligente.</Say>
  <Gather input="dtmf" timeout="10" numDigits="1">
    <Say voice="alice" language="pt-BR">Pressione qualquer tecla para continuar ou aguarde para falar com o assistente virtual.</Say>
  </Gather>
  <Say voice="alice" language="pt-BR">Obrigado por usar o sistema Abmix. A chamada será mantida ativa para testes. Pressione desligar no painel para encerrar.</Say>
  <Pause length="300"/>
</Response>`);
  });

  return server;
}

server/storage.ts.content:
import { type Favorite, type InsertFavorite, type Call, type InsertCall, type Transcript, type InsertTranscript, type Prompt, type InsertPrompt } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Favorites
  getFavorites(): Promise<Favorite[]>;
  getFavorite(id: string): Promise<Favorite | undefined>;
  createFavorite(favorite: InsertFavorite): Promise<Favorite>;
  updateFavorite(id: string, favorite: Partial<InsertFavorite>): Promise<Favorite | undefined>;
  deleteFavorite(id: string): Promise<boolean>;

  // Calls
  getCalls(): Promise<Call[]>;
  getCall(id: string): Promise<Call | undefined>;
  createCall(call: InsertCall): Promise<Call>;
  updateCall(id: string, call: Partial<Call>): Promise<Call | undefined>;

  // Transcripts
  getTranscripts(callId: string): Promise<Transcript[]>;
  createTranscript(transcript: InsertTranscript): Promise<Transcript>;

  // Prompts
  getPrompts(callId: string): Promise<Prompt[]>;
  createPrompt(prompt: InsertPrompt): Promise<Prompt>;
  updatePrompt(id: string, prompt: Partial<Prompt>): Promise<Prompt | undefined>;
}

export class MemStorage implements IStorage {
  private favorites: Map<string, Favorite>;
  private calls: Map<string, Call>;
  private transcripts: Map<string, Transcript>;
  private prompts: Map<string, Prompt>;

  constructor() {
    this.favorites = new Map();
    this.calls = new Map();
    this.transcripts = new Map();
    this.prompts = new Map();
  }

  // Favorites
  async getFavorites(): Promise<Favorite[]> {
    return Array.from(this.favorites.values());
  }

  async getFavorite(id: string): Promise<Favorite | undefined> {
    return this.favorites.get(id);
  }

  async createFavorite(insertFavorite: InsertFavorite): Promise<Favorite> {
    const id = randomUUID();
    const favorite: Favorite = {
      ...insertFavorite,
      id,
      createdAt: new Date(),
      tags: insertFavorite.tags || null,
      notes: insertFavorite.notes || null,
    };
    this.favorites.set(id, favorite);
    return favorite;
  }

  async updateFavorite(id: string, updateData: Partial<InsertFavorite>): Promise<Favorite | undefined> {
    const existing = this.favorites.get(id);
    if (!existing) return undefined;

    const updated: Favorite = { ...existing, ...updateData };
    this.favorites.set(id, updated);
    return updated;
  }

  async deleteFavorite(id: string): Promise<boolean> {
    return this.favorites.delete(id);
  }

  // Calls
  async getCalls(): Promise<Call[]> {
    return Array.from(this.calls.values());
  }

  async getCall(id: string): Promise<Call | undefined> {
    return this.calls.get(id);
  }

  async createCall(insertCall: InsertCall): Promise<Call> {
    const id = randomUUID();
    const call: Call = {
      ...insertCall,
      id,
      startTime: new Date(),
      endTime: null,
      duration: null,
      recordingUrl: null,
      metadata: null,
      fromNumber: insertCall.fromNumber || null,
    };
    this.calls.set(id, call);
    return call;
  }

  async updateCall(id: string, updateData: Partial<Call>): Promise<Call | undefined> {
    const existing = this.calls.get(id);
    if (!existing) return undefined;

    const updated: Call = { ...existing, ...updateData };
    this.calls.set(id, updated);
    return updated;
  }

  // Transcripts
  async getTranscripts(callId: string): Promise<Transcript[]> {
    return Array.from(this.transcripts.values()).filter(t => t.callId === callId);
  }

  async createTranscript(insertTranscript: InsertTranscript): Promise<Transcript> {
    const id = randomUUID();
    const transcript: Transcript = {
      ...insertTranscript,
      id,
      timestamp: new Date(),
      callId: insertTranscript.callId || null,
      isFinal: insertTranscript.isFinal || null,
      confidence: insertTranscript.confidence || null,
    };
    this.transcripts.set(id, transcript);
    return transcript;
  }

  // Prompts
  async getPrompts(callId: string): Promise<Prompt[]> {
    return Array.from(this.prompts.values()).filter(p => p.callId === callId);
  }

  async createPrompt(insertPrompt: InsertPrompt): Promise<Prompt> {
    const id = randomUUID();
    const prompt: Prompt = {
      ...insertPrompt,
      id,
      timestamp: new Date(),
      applied: false,
      callId: insertPrompt.callId || null,
    };
    this.prompts.set(id, prompt);
    return prompt;
  }

  async updatePrompt(id: string, updateData: Partial<Prompt>): Promise<Prompt | undefined> {
    const existing = this.prompts.get(id);
    if (!existing) return undefined;

    const updated: Prompt = { ...existing, ...updateData };
    this.prompts.set(id, updated);
    return updated;
  }
}

export const storage = new MemStorage();

server/telephony.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer } from "ws";
import twilio from "twilio";
import { elevenLabsService } from "./elevenlabs";
import { queries } from "./database";
import { writeFileSync, statSync } from "fs";
import { join } from "path";

// Environment variables
const {
  TWILIO_ACCOUNT_SID,
  TWILIO_AUTH_TOKEN,
  TWILIO_NUMBER,
  ELEVENLABS_API_KEY,
  REPLIT_DEV_DOMAIN
} = process.env;

const PUBLIC_BASE_URL = `https://${REPLIT_DEV_DOMAIN}`;

// Initialize Twilio client
const twilioClient = twilio(TWILIO_ACCOUNT_SID, TWILIO_AUTH_TOKEN);

// Active calls and sessions tracking
const activeCalls = new Map();
const activeRecordings = new Map();
const mediaStreams = new Map();

// Latency tracking for metrics
let latencyMetrics: number[] = [];

export function setupTelephony(app: Express, httpServer: Server) {
  // WebSocket servers
  const captionsWss = new WebSocketServer({ server: httpServer, path: '/captions' });
  const mediaWss = new WebSocketServer({ server: httpServer, path: '/media' });

  console.log('[TELEPHONY] WebSocket servers initialized on /captions and /media');

  // === TELEPHONY ROUTES ===

  // Start call
  app.post("/api/call/dial", async (req, res) => {
    try {
      const { to, voiceType = 'masc' } = req.body;

      if (!to) {
        return res.status(400).json({ message: "Phone number 'to' is required" });
      }

      console.log(`[CALL] Starting call to ${to} with voice type: ${voiceType}`);

      const call = await twilioClient.calls.create({
        to,
        from: TWILIO_NUMBER!,
        url: `${PUBLIC_BASE_URL}/twiml?voiceType=${voiceType}`,
        record: false // Recording controlled via API
      });

      // Store call session
      activeCalls.set(call.sid, {
        callSid: call.sid,
        to,
        voiceType,
        status: 'initiated',
        startTime: new Date()
      });

      console.log(`[CALL] Call initiated with SID: ${call.sid}`);

      res.json({ 
        callSid: call.sid, 
        status: call.status,
        to,
        voiceType 
      });
    } catch (error) {
      console.error('[CALL] Error starting call:', error);
      res.status(500).json({ message: "Failed to start call", error: error instanceof Error ? error.message : String(error) });
    }
  });

  // Hangup call
  app.post("/api/call/hangup", async (req, res) => {
    try {
      const { callSid } = req.body;

      if (!callSid) {
        return res.status(400).json({ message: "callSid is required" });
      }

      console.log(`[CALL] Hanging up call: ${callSid}`);

      await twilioClient.calls(callSid).update({ status: 'completed' });

      // Clean up session
      activeCalls.delete(callSid);

      console.log(`[CALL] Call ${callSid} hung up successfully`);

      res.json({ message: "Call ended successfully", callSid });
    } catch (error) {
      console.error('[CALL] Error hanging up call:', error);
      res.status(500).json({ message: "Failed to hangup call", error: error instanceof Error ? error.message : String(error) });
    }
  });

  // TwiML endpoint with recording
  app.all("/twiml", (req, res) => {
    console.log(`[TWIML] Serving TwiML response`);

    const twiml = `<?xml version="1.0" encoding="UTF-8"?>
<Response>
  <Start>
    <Recording trim="do-not-trim"
              recordingStatusCallback="https://disckmix.replit.app/twilio/recording-status"
              recordingStatusCallbackEvent="completed failed"
              recordingTrack="both"
              playBeep="false"/>
  </Start>
  <Connect>
    <Stream url="wss://disckmix.replit.app/media"/>
  </Connect>
</Response>`;

    res.type('text/xml').send(twiml);
  });

  // === WEBSOCKET HANDLERS ===

  // Handle captions WebSocket connections
  captionsWss.on('connection', (ws, req) => {
    console.log('[CAPTIONS] Client connected to captions WebSocket');

    // Send welcome message
    ws.send(JSON.stringify({
      text: 'Sistema de legendas conectado',
      isFinal: false,
      timestamp: Date.now()
    }));

    // Handle connection close
    ws.on('close', () => {
      console.log('[CAPTIONS] Captions WebSocket client disconnected');
    });

    // Handle errors
    ws.on('error', (error) => {
      console.error('[CAPTIONS] Captions WebSocket error:', error);
    });
  });

  // Handle media WebSocket connections from Twilio
  mediaWss.on('connection', (ws, req) => {
    console.log('[MEDIA] Twilio media stream connected');

    ws.on('message', (message) => {
      try {
        const data = JSON.parse(message.toString());

        if (data.event === 'connected') {
          console.log('[MEDIA] Media stream connected for call:', data.callSid);
          mediaStreams.set(data.callSid, ws);
        } else if (data.event === 'media') {
          // Forward audio to STT service
          // ElevenLabs STT integration would go here
        }
      } catch (error) {
        console.error('[MEDIA] Error processing media message:', error);
      }
    });

    ws.on('close', () => {
      console.log('[MEDIA] Media stream disconnected');
    });

    ws.on('error', (error) => {
      console.error('[MEDIA] Media stream error:', error);
    });
  });

  // Forward STT results to captions WebSocket
  elevenLabsService.on('stt-transcript', (transcript) => {
    captionsWss.clients.forEach((client) => {
      if (client.readyState === client.OPEN) {
        client.send(JSON.stringify(transcript));
      }
    });
  });

  // === AI/PROMPT ROUTES ===

  // Send live prompt
  app.post("/api/ai/prompt", (req, res) => {
    try {
      const { text, style = 'neutro' } = req.body;

      if (!text) {
        return res.status(400).json({ message: "text is required" });
      }

      console.log(`[AI] Sending live prompt: "${text}" with style: ${style}`);

      // Find active TTS sessions and send prompt
      let sentCount = 0;
      activeCalls.forEach((callData, callSid) => {
        if (elevenLabsService.sendTTSText(callSid, text, style)) {
          sentCount++;
        }
      });

      res.json({ 
        message: "Prompt sent", 
        text, 
        style, 
        activeSessions: sentCount 
      });
    } catch (error) {
      console.error('[AI] Error sending prompt:', error);
      res.status(500).json({ message: "Failed to send prompt", error: error instanceof Error ? error.message : String(error) });
    }
  });

  // === VOICE/SETTINGS ROUTES ===

  // Get voices from ElevenLabs
  app.get("/api/voices", async (req, res) => {
    try {
      const voices = await elevenLabsService.getVoices();
      res.json(voices);
    } catch (error) {
      console.error('[VOICES] Error fetching voices:', error);
      res.status(500).json({ message: "Failed to fetch voices", error: error instanceof Error ? error.message : String(error) });
    }
  });

  // Get settings
  app.get("/api/settings", (req, res) => {
    try {
      const settings = queries.getAllSettings.all();
      const settingsObj = settings.reduce((acc: any, setting: any) => {
        acc[setting.key] = setting.value;
        return acc;
      }, {});

      res.json(settingsObj);
    } catch (error) {
      console.error('[SETTINGS] Error getting settings:', error);
      res.status(500).json({ message: "Failed to get settings", error: error instanceof Error ? error.message : String(error) });
    }
  });

  // Update settings
  app.post("/api/settings", (req, res) => {
    try {
      const settings = req.body;

      for (const [key, value] of Object.entries(settings)) {
        queries.setSetting.run(key, value);
      }

      console.log('[SETTINGS] Updated settings:', Object.keys(settings));
      res.json({ message: "Settings updated", keys: Object.keys(settings) });
    } catch (error) {
      console.error('[SETTINGS] Error updating settings:', error);
      res.status(500).json({ message: "Failed to update settings", error: error instanceof Error ? error.message : String(error) });
    }
  });

  // === METRICS ROUTE ===

  // SSE endpoint for real-time metrics
  app.get("/api/metrics", (req, res) => {
    res.writeHead(200, {
      'Content-Type': 'text/event-stream',
      'Cache-Control': 'no-cache',
      'Connection': 'keep-alive',
      'Access-Control-Allow-Origin': '*'
    });

    const sendMetrics = () => {
      const latency = elevenLabsService.getAverageLatency();
      const data = {
        latency,
        activeCalls: activeCalls.size,
        activeRecordings: activeRecordings.size,
        timestamp: Date.now()
      };

      res.write(`data: ${JSON.stringify(data)}\n\n`);
    };

    // Send initial metrics
    sendMetrics();

    // Send metrics every 2 seconds
    const interval = setInterval(sendMetrics, 2000);

    req.on('close', () => {
      clearInterval(interval);
    });
  });

  // === WEBSOCKET HEARTBEAT ===

  // Heartbeat to keep WS alive in Replit
  setInterval(() => {
    captionsWss.clients.forEach((ws: any) => {
      if (!ws.isAlive) return ws.terminate();
      ws.isAlive = false;
      try { 
        ws.ping(); 
      } catch (e) {
        // Ignore ping errors
      }
    });
  }, 25000);

  // Media Stream WebSocket for Twilio
  mediaWss.on('connection', (ws, req) => {
    console.log('[MEDIA] Twilio Media Stream connected');

    let callSid: string | null = null;

    ws.on('message', async (message) => {
      try {
        const data = JSON.parse(message.toString());

        switch (data.event) {
          case 'connected':
            console.log('[MEDIA] Media stream connected');
            break;

          case 'start':
            if (data.start && data.start.callSid) {
              callSid = data.start.callSid;
              console.log(`[MEDIA] Media stream started for call: ${callSid}`);
              console.log('[MEDIA] Start event data:', JSON.stringify(data.start, null, 2));
            }
            break;

          case 'media':
            // Log media reception (without flooding console)
            if (Math.random() < 0.001) { // Log ~0.1% of media packets
              console.log(`[MEDIA] Receiving audio data for call: ${callSid}`);
            }
            break;

          case 'stop':
            console.log(`[MEDIA] Media stream stopped for call: ${callSid}`);
            break;

          default:
            console.log(`[MEDIA] Received event: ${data.event}`);
        }
      } catch (error) {
        console.error('[MEDIA] Error processing message:', error);
      }
    });

    ws.on('close', () => {
      console.log(`[MEDIA] Media stream disconnected for call: ${callSid}`);
    });

    ws.on('error', (error) => {
      console.error('[MEDIA] WebSocket error:', error);
    });
  });

  // === FAVORITES ROUTES ===

  // List favorites
  app.get("/api/favorites", (req, res) => {
    try {
      const favorites = queries.getAllFavorites.all();
      res.json(favorites);
    } catch (error) {
      console.error('[FAVORITES] Error listing favorites:', error);
      res.status(500).json({ message: "Failed to list favorites", error: error instanceof Error ? error.message : String(error) });
    }
  });

  // Add favorite
  app.post("/api/favorites/add", (req, res) => {
    try {
      const { name, phone_e164, voice_type = 'masc' } = req.body;

      if (!name || !phone_e164) {
        return res.status(400).json({ message: "name and phone_e164 are required" });
      }

      queries.addFavorite.run(name, phone_e164, voice_type);

      console.log(`[FAVORITES] Added favorite: ${name} - ${phone_e164} (${voice_type})`);
      res.json({ message: "Favorite added", name, phone_e164, voice_type });
    } catch (error) {
      console.error('[FAVORITES] Error adding favorite:', error);
      res.status(500).json({ message: "Failed to add favorite", error: error instanceof Error ? error.message : String(error) });
    }
  });

  // Remove favorite
  app.post("/api/favorites/remove", (req, res) => {
    try {
      const { id } = req.body;

      if (!id) {
        return res.status(400).json({ message: "id is required" });
      }

      queries.removeFavorite.run(id);

      console.log(`[FAVORITES] Removed favorite with ID: ${id}`);
      res.json({ message: "Favorite removed", id });
    } catch (error) {
      console.error('[FAVORITES] Error removing favorite:', error);
      res.status(500).json({ message: "Failed to remove favorite", error: error instanceof Error ? error.message : String(error) });
    }
  });

  // === TWILIO CALLBACK ROUTES ===

  // Twilio recording status callback
  app.post("/twilio/recording-status", (req, res) => {
    console.log('[TWILIO] Recording status callback:', req.body);
    res.status(200).send('OK');
  });

  // Twilio call status callback
  app.post("/twilio/call-status", (req, res) => {
    console.log('[TWILIO] Call status callback:', req.body);
    res.status(200).send('OK');
  });

  console.log('[TELEPHONY] WebSocket servers initialized on /captions and /media');
  return { captionsWss, mediaWss };
}

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

