import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { 
  Dot, 
  Square, 
  Play, 
  Pause, 
  Download, 
  Trash2, 
  Clock, 
  Phone,
  FileAudio,
  Volume2
} from "lucide-react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";
import { apiRequest } from "@/lib/queryClient";

interface Recording {
  id: string;
  filename: string;
  status: 'recording' | 'paused' | 'completed' | 'failed';
  startTime: string;
  endTime?: string;
  duration?: number;
  fileSize?: number;
  transcription?: string;
  callId?: string;
  toNumber?: string;
  fromNumber?: string;
}

interface CallRecordingProps {
  callSid?: string;
  phoneNumber?: string;
  isCallActive: boolean;
}

export function CallRecording({ callSid, phoneNumber, isCallActive }: CallRecordingProps) {
  const { toast } = useToast();
  const [currentRecording, setCurrentRecording] = useState<Recording | null>(null);
  const [recordingProgress, setRecordingProgress] = useState(0);
  const [playingAudio, setPlayingAudio] = useState<string | null>(null);

  // Fetch all recordings
  const { data: recordings = [], refetch: refetchRecordings } = useQuery<Recording[]>({
    queryKey: ['/api/recordings/list'],
    refetchInterval: currentRecording?.status === 'recording' ? 2000 : false,
  });

  // Start recording mutation
  const startRecordingMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('POST', '/api/recordings/start', {
        callSid,
        phoneNumber
      });
      return response.json();
    },
    onSuccess: (data) => {
      setCurrentRecording(data);
      toast({
        title: "🔴 Gravação Iniciada",
        description: `Gravando chamada para ${phoneNumber}`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/recordings/list'] });
    },
    onError: (error) => {
      toast({
        title: "Erro na Gravação",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Pause recording mutation
  const pauseRecordingMutation = useMutation({
    mutationFn: async (recordingId: string) => {
      const response = await apiRequest('POST', `/api/recordings/${recordingId}/pause`);
      return response.json();
    },
    onSuccess: () => {
      setCurrentRecording(prev => prev ? { ...prev, status: 'paused' } : null);
      toast({
        title: "⏸️ Gravação Pausada",
        description: "A gravação foi pausada",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/recordings/list'] });
    },
    onError: (error) => {
      toast({
        title: "Erro",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Resume recording mutation
  const resumeRecordingMutation = useMutation({
    mutationFn: async (recordingId: string) => {
      const response = await apiRequest('POST', `/api/recordings/${recordingId}/resume`);
      return response.json();
    },
    onSuccess: () => {
      setCurrentRecording(prev => prev ? { ...prev, status: 'recording' } : null);
      toast({
        title: "▶️ Gravação Retomada",
        description: "A gravação foi retomada",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/recordings/list'] });
    },
    onError: (error) => {
      toast({
        title: "Erro",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Stop recording mutation
  const stopRecordingMutation = useMutation({
    mutationFn: async (recordingId: string) => {
      const response = await apiRequest('POST', `/api/recordings/${recordingId}/stop`);
      return response.json();
    },
    onSuccess: (data) => {
      setCurrentRecording(null);
      setRecordingProgress(0);
      toast({
        title: "⏹️ Gravação Finalizada",
        description: `Arquivo salvo: ${data.filename}`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/recordings/list'] });
    },
    onError: (error) => {
      toast({
        title: "Erro",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Delete recording mutation
  const deleteRecordingMutation = useMutation({
    mutationFn: async (recordingId: string) => {
      const response = await apiRequest('DELETE', `/api/recordings/${recordingId}`);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "🗑️ Gravação Removida",
        description: "A gravação foi removida com sucesso",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/recordings/list'] });
    },
    onError: (error) => {
      toast({
        title: "Erro",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Update recording progress
  useEffect(() => {
    let interval: NodeJS.Timeout;

    if (currentRecording?.status === 'recording') {
      interval = setInterval(() => {
        setRecordingProgress(prev => Math.min(prev + 1, 100));
      }, 1000);
    }

    return () => {
      if (interval) clearInterval(interval);
    };
  }, [currentRecording?.status]);

  // Play audio function
  const playAudio = async (recordingId: string, filename: string) => {
    try {
      if (playingAudio === recordingId) {
        setPlayingAudio(null);
        return;
      }

      setPlayingAudio(recordingId);

      const response = await fetch(`/api/recordings/${recordingId}/download`);
      const blob = await response.blob();
      const audioUrl = URL.createObjectURL(blob);
      const audio = new Audio(audioUrl);

      audio.onended = () => {
        setPlayingAudio(null);
        URL.revokeObjectURL(audioUrl);
      };

      audio.onerror = () => {
        setPlayingAudio(null);
        URL.revokeObjectURL(audioUrl);
        toast({
          title: "Erro na Reprodução",
          description: "Não foi possível reproduzir a gravação",
          variant: "destructive",
        });
      };

      await audio.play();
    } catch (error) {
      setPlayingAudio(null);
      toast({
        title: "Erro na Reprodução",
        description: "Falha ao reproduzir áudio",
        variant: "destructive",
      });
    }
  };

  const downloadRecording = (recordingId: string, filename: string) => {
    const link = document.createElement('a');
    link.href = `/api/recordings/${recordingId}/download`;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const formatDuration = (seconds?: number) => {
    if (!seconds) return "00:00";
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const formatFileSize = (bytes?: number) => {
    if (!bytes) return "0 B";
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(1024));
    return Math.round(bytes / Math.pow(1024, i) * 100) / 100 + ' ' + sizes[i];
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'recording': return 'bg-red-500';
      case 'paused': return 'bg-yellow-500';
      case 'completed': return 'bg-green-500';
      case 'failed': return 'bg-gray-500';
      default: return 'bg-gray-400';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'recording': return 'Gravando';
      case 'paused': return 'Pausada';
      case 'completed': return 'Concluída';
      case 'failed': return 'Falhou';
      default: return 'Desconhecido';
    }
  };

  return (
    <div className="space-y-6">
      {/* Current Recording Controls */}
      {isCallActive && (
        <Card className="border-red-200 dark:border-red-800">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-red-700 dark:text-red-300">
              <Dot className="h-5 w-5" />
              Controles de Gravação
            </CardTitle>
            <CardDescription>
              Gerencie a gravação da chamada atual para {phoneNumber}
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            {!currentRecording ? (
              <Button 
                onClick={() => startRecordingMutation.mutate()}
                disabled={startRecordingMutation.isPending}
                className="w-full bg-red-600 hover:bg-red-700"
                data-testid="button-start-recording"
              >
                <Dot className="h-4 w-4 mr-2" />
                Iniciar Gravação
              </Button>
            ) : (
              <div className="space-y-4">
                <div className="flex items-center gap-2">
                  <Badge className={getStatusColor(currentRecording.status)} data-testid="badge-recording-status">
                    {getStatusText(currentRecording.status)}
                  </Badge>
                  <span className="text-sm font-mono" data-testid="text-recording-duration">
                    {Math.floor(recordingProgress / 60).toString().padStart(2, '0')}:
                    {(recordingProgress % 60).toString().padStart(2, '0')}
                  </span>
                </div>

                {currentRecording.status === 'recording' && (
                  <Progress value={recordingProgress} className="w-full" />
                )}

                <div className="flex gap-2">
                  {currentRecording.status === 'recording' ? (
                    <Button
                      onClick={() => pauseRecordingMutation.mutate(currentRecording.id)}
                      disabled={pauseRecordingMutation.isPending}
                      variant="outline"
                      data-testid="button-pause-recording"
                    >
                      <Pause className="h-4 w-4 mr-2" />
                      Pausar
                    </Button>
                  ) : currentRecording.status === 'paused' ? (
                    <Button
                      onClick={() => resumeRecordingMutation.mutate(currentRecording.id)}
                      disabled={resumeRecordingMutation.isPending}
                      variant="outline"
                      data-testid="button-resume-recording"
                    >
                      <Play className="h-4 w-4 mr-2" />
                      Continuar
                    </Button>
                  ) : null}

                  <Button
                    onClick={() => stopRecordingMutation.mutate(currentRecording.id)}
                    disabled={stopRecordingMutation.isPending}
                    variant="destructive"
                    data-testid="button-stop-recording"
                  >
                    <Square className="h-4 w-4 mr-2" />
                    Finalizar
                  </Button>
                </div>
              </div>
            )}
          </CardContent>
        </Card>
      )}

      {/* Recordings List */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <FileAudio className="h-5 w-5" />
            Gravações Salvas
          </CardTitle>
          <CardDescription>
            Histórico de gravações de chamadas com reprodução e download
          </CardDescription>
        </CardHeader>
        <CardContent>
          {recordings.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground" data-testid="text-no-recordings">
              <FileAudio className="h-12 w-12 mx-auto mb-4 opacity-50" />
              <p>Nenhuma gravação disponível</p>
              <p className="text-sm">As gravações aparecerão aqui após serem finalizadas</p>
            </div>
          ) : (
            <div className="space-y-4">
              {recordings.map((recording) => (
                <div 
                  key={recording.id} 
                  className="flex items-center justify-between p-4 border rounded-lg"
                  data-testid={`recording-item-${recording.id}`}
                >
                  <div className="flex items-center gap-4">
                    <div className="flex flex-col items-center">
                      <Badge className={getStatusColor(recording.status)} data-testid={`badge-status-${recording.id}`}>
                        {getStatusText(recording.status)}
                      </Badge>
                      <div className="text-xs text-muted-foreground mt-1">
                        <Clock className="h-3 w-3 inline mr-1" />
                        {formatDuration(recording.duration)}
                      </div>
                    </div>

                    <div className="space-y-1">
                      <div className="font-medium" data-testid={`text-filename-${recording.id}`}>
                        {recording.filename}
                      </div>
                      <div className="text-sm text-muted-foreground flex items-center gap-2">
                        <Phone className="h-3 w-3" />
                        {recording.toNumber && (
                          <span data-testid={`text-number-${recording.id}`}>
                            Para: {recording.toNumber}
                          </span>
                        )}
                        {recording.fromNumber && (
                          <span>De: {recording.fromNumber}</span>
                        )}
                      </div>
                      <div className="text-xs text-muted-foreground">
                        {new Date(recording.startTime).toLocaleString('pt-BR')}
                        {recording.fileSize && ` • ${formatFileSize(recording.fileSize)}`}
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center gap-2">
                    {recording.status === 'completed' && (
                      <>
                        <Button
                          onClick={() => playAudio(recording.id, recording.filename)}
                          variant="outline"
                          size="sm"
                          disabled={!!playingAudio && playingAudio !== recording.id}
                          data-testid={`button-play-${recording.id}`}
                        >
                          {playingAudio === recording.id ? (
                            <>
                              <Volume2 className="h-4 w-4 mr-2" />
                              Tocando...
                            </>
                          ) : (
                            <>
                              <Play className="h-4 w-4 mr-2" />
                              Reproduzir
                            </>
                          )}
                        </Button>

                        <Button
                          onClick={() => downloadRecording(recording.id, recording.filename)}
                          variant="outline"
                          size="sm"
                          data-testid={`button-download-${recording.id}`}
                        >
                          <Download className="h-4 w-4 mr-2" />
                          Download
                        </Button>
                      </>
                    )}

                    <Button
                      onClick={() => deleteRecordingMutation.mutate(recording.id)}
                      disabled={deleteRecordingMutation.isPending}
                      variant="outline"
                      size="sm"
                      className="text-red-600 hover:text-red-700"
                      data-testid={`button-delete-${recording.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/DTMFKeypad.tsx.content:
import { useMutation } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { useToast } from '@/hooks/use-toast';
import { useCallStore } from '@/stores/useCallStore';
import { api } from '@/services/api';

export function DTMFKeypad() {
  const { toast } = useToast();
  const { currentCallId, callState } = useCallStore();

  const sendDTMFMutation = useMutation({
    mutationFn: (digits: string) => api.sendDTMF(currentCallId!, digits),
    onSuccess: (data, digits) => {
      toast({
        title: "DTMF enviado",
        description: `Tom "${digits}" transmitido`,
      });
    },
    onError: (error) => {
      toast({
        title: "Erro ao enviar DTMF",
        description: error instanceof Error ? error.message : "Falha na transmissão",
        variant: "destructive",
      });
    }
  });

  const handleKeyPress = (digit: string) => {
    if (!currentCallId) {
      toast({
        title: "Nenhuma chamada ativa",
        description: "Inicie uma chamada para usar o teclado",
        variant: "destructive",
      });
      return;
    }

    if (callState !== 'CONNECTED') {
      toast({
        title: "Chamada não conectada",
        description: "Aguarde a conexão para usar o teclado",
        variant: "destructive",
      });
      return;
    }

    sendDTMFMutation.mutate(digit);
  };

  const keypadLayout = [
    ['1', '2', '3'],
    ['4', '5', '6'],
    ['7', '8', '9'],
    ['*', '0', '#']
  ];

  const isDisabled = !currentCallId || callState !== 'CONNECTED' || sendDTMFMutation.isPending;

  return (
    <div className="bg-dark-surface rounded-xl p-6 border border-dark-border">
      <h3 className="text-lg font-semibold mb-4">Teclado DTMF</h3>

      <div className="grid grid-cols-3 gap-3">
        {keypadLayout.flat().map((digit) => (
          <Button
            key={digit}
            variant="outline"
            onClick={() => handleKeyPress(digit)}
            disabled={isDisabled}
            className="h-12 text-lg font-semibold bg-dark-bg hover:bg-abmix-green hover:text-black border-dark-border disabled:opacity-50"
            data-testid={`dtmf-${digit}`}
          >
            {digit}
          </Button>
        ))}
      </div>

      {isDisabled && (
        <p className="text-sm text-gray-400 mt-3 text-center">
          {!currentCallId ? 'Inicie uma chamada para usar o teclado' : 
           callState !== 'CONNECTED' ? 'Aguarde a conexão' : 
           'Enviando...'}
        </p>
      )}
    </div>
  );
}

client/src/components/DialerCard.tsx.content:
import { useState, useEffect } from 'react';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { useToast } from '@/hooks/use-toast';
import { useCallStore } from '@/stores/useCallStore';
import { api } from '@/services/api';
import { connectCaptions, disconnectCaptions } from '@/services/captions';
import { validateE164, formatPhoneNumber, isDTMFTone } from '@/utils/validation';

export function DialerCard() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { 
    phoneNumber, 
    setPhoneNumber, 
    appendToPhoneNumber,
    clearPhoneNumber,
    callState, 
    setCallState, 
    setCurrentCallId,
    currentCallId,
    voiceType,
    setVoiceType,
    selectedProvider 
  } = useCallStore();

  const [selectedVoice, setSelectedVoice] = useState<'masc' | 'fem'>(voiceType as 'masc' | 'fem');

  // Listen for favorite number selection
  useEffect(() => {
    const handleSetDialerNumber = (event: any) => {
      const { number } = event.detail;
      setPhoneNumber(number);
    };

    window.addEventListener('setDialerNumber', handleSetDialerNumber);
    return () => window.removeEventListener('setDialerNumber', handleSetDialerNumber);
  }, [setPhoneNumber]);

  // Setup caption service only when needed (during call)
  useEffect(() => {
    if (callState === 'CONNECTED') {
      (window as any).__CALL_ACTIVE__ = true;
      connectCaptions();
    } else {
      (window as any).__CALL_ACTIVE__ = false;
      disconnectCaptions();
    }

    return () => {
      if (callState !== 'CONNECTED') {
        (window as any).__CALL_ACTIVE__ = false;
        disconnectCaptions();
      }
    };
  }, [callState]);

  const startCallMutation = useMutation({
    mutationFn: () => api.startCall(selectedProvider, phoneNumber, selectedVoice),
    onSuccess: (data) => {
      setCurrentCallId(data.callSid);
      setCallState('RINGING');
      setVoiceType(selectedVoice);

      // Connect captions when call starts
      (window as any).__CALL_ACTIVE__ = true;
      connectCaptions();

      toast({
        title: "Chamada iniciada",
        description: `Discando para ${phoneNumber} (voz ${selectedVoice === 'masc' ? 'masculina' : 'feminina'})`,
      });
    },
    onError: (error) => {
      toast({
        title: "Erro ao iniciar chamada",
        description: error instanceof Error ? error.message : "Falha na discagem",
        variant: "destructive",
      });
    }
  });

  const hangupMutation = useMutation({
    mutationFn: () => api.hangupCall(currentCallId!),
    onSuccess: () => {
      setCallState('ENDED');

      // Disconnect captions when call ends
      (window as any).__CALL_ACTIVE__ = false;
      disconnectCaptions();

      setTimeout(() => {
        setCallState('IDLE');
        setCurrentCallId(null);
      }, 1000);
      toast({
        title: "Chamada encerrada",
      });
    },
    onError: (error) => {
      toast({
        title: "Erro ao encerrar chamada",
        description: error instanceof Error ? error.message : "Falha ao encerrar",
        variant: "destructive",
      });
    }
  });

  const dtmfMutation = useMutation({
    mutationFn: (tone: string) => api.sendDTMF(currentCallId!, tone),
    onSuccess: (_, tone) => {
      toast({
        title: `DTMF enviado: ${tone}`,
      });
    },
    onError: (error) => {
      toast({
        title: "Erro ao enviar DTMF",
        description: error instanceof Error ? error.message : "Falha no envio",
        variant: "destructive",
      });
    }
  });

  const handleDial = () => {
    const formatted = formatPhoneNumber(phoneNumber);

    if (!validateE164(formatted)) {
      toast({
        title: "Número inválido",
        description: "Por favor, insira um número no formato E.164 (+5511999999999)",
        variant: "destructive",
      });
      return;
    }

    setPhoneNumber(formatted);
    startCallMutation.mutate();
  };

  const handleAnswer = () => {
    // For incoming calls - this would be implemented when handling inbound calls
    setCallState('CONNECTED');
    toast({
      title: "Chamada atendida",
    });
  };

  const handleHangup = () => {
    if (currentCallId) {
      hangupMutation.mutate();
    } else {
      setCallState('IDLE');
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && callState === 'IDLE') {
      handleDial();
    }
  };

  const sendDTMF = (tone: string) => {
    if (!currentCallId) {
      toast({
        title: "Nenhuma chamada ativa",
        variant: "destructive",
      });
      return;
    }

    if (!isDTMFTone(tone)) {
      toast({
        title: "Tom DTMF inválido",
        variant: "destructive",
      });
      return;
    }

    dtmfMutation.mutate(tone);
  };

  const dtmfTones = ['1', '2', '3', '4', '5', '6', '7', '8', '9', '*', '0', '#'];

  // Unified keypad handler - works for both dialing and DTMF
  const handleKeypadPress = (digit: string) => {
    if (callState === 'IDLE') {
      // During dialing - add digit to phone number
      if (digit === '*') {
        appendToPhoneNumber('+');
      } else if (digit === '#') {
        // # doesn't add anything during dialing
        return;
      } else {
        appendToPhoneNumber(digit);
      }
    } else if (callState === 'CONNECTED') {
      // During call - send DTMF tone
      sendDTMF(digit);
    } else {
      toast({
        title: "Teclado indisponível",
        description: "Use o teclado apenas durante discagem ou chamada conectada",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="bg-card rounded-xl p-6 border border-border">
      <h3 className="text-lg font-semibold mb-4 text-foreground">Discagem</h3>

      {/* Number Input */}
      <div className="mb-4">
        <Label htmlFor="phone-input" className="block text-sm text-muted-foreground mb-2">
          Número (E.164)
        </Label>
        <Input
          id="phone-input"
          type="text"
          placeholder="+5511999999999"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          onKeyDown={handleKeyDown}
          className="w-full bg-background border border-border rounded-lg px-4 py-3 focus:border-abmix-green focus:outline-none transition-colors font-mono"
          data-testid="phone-input"
        />
      </div>

      {/* Voice Selection */}
      <div className="mb-4">
        <Label className="block text-sm text-muted-foreground mb-2">Tipo de Voz</Label>
        <div className="grid grid-cols-2 gap-2">
          <Button
            onClick={() => {
              setSelectedVoice('masc');
              toast({ title: "Voz selecionada", description: "Voz masculina ativada" });
            }}
            className={`py-2 px-3 rounded-lg transition-colors flex flex-col items-center justify-center text-xs gap-1 ${
              selectedVoice === 'masc' 
                ? 'bg-abmix-green text-black' 
                : 'bg-background border border-border text-foreground hover:bg-muted'
            }`}
            data-testid="male-voice-button"
          >
            <i className="fas fa-male text-sm"></i>
            Masculina
          </Button>

          <Button
            onClick={() => {
              setSelectedVoice('fem');
              toast({ title: "Voz selecionada", description: "Voz feminina ativada" });
            }}
            className={`py-2 px-3 rounded-lg transition-colors flex flex-col items-center justify-center text-xs gap-1 ${
              selectedVoice === 'fem' 
                ? 'bg-abmix-green text-black' 
                : 'bg-background border border-border text-foreground hover:bg-muted'
            }`}
            data-testid="female-voice-button"
          >
            <i className="fas fa-female text-sm"></i>
            Feminina
          </Button>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="grid grid-cols-3 gap-2">
        <Button
          onClick={handleDial}
          disabled={callState !== 'IDLE' || startCallMutation.isPending}
          className="bg-abmix-green text-black font-medium py-2 px-2 rounded-lg hover:bg-abmix-green/90 transition-colors flex flex-col items-center justify-center text-xs gap-1"
          data-testid="dial-button"
        >
          <i className="fas fa-phone text-sm"></i>
          {startCallMutation.isPending ? 'Discando' : 'Discar'}
        </Button>

        <Button
          onClick={handleAnswer}
          disabled={callState !== 'RINGING'}
          className="bg-abmix-green text-black font-medium py-2 px-2 rounded-lg hover:bg-abmix-green/90 transition-colors flex flex-col items-center justify-center disabled:opacity-50 text-xs gap-1"
          data-testid="answer-button"
        >
          <i className="fas fa-phone text-sm"></i>
          Atender
        </Button>

        <Button
          onClick={handleHangup}
          disabled={callState === 'IDLE' || hangupMutation.isPending}
          className="bg-abmix-green text-black font-medium py-2 px-2 rounded-lg hover:bg-abmix-green/90 transition-colors flex flex-col items-center justify-center disabled:opacity-50 text-xs gap-1"
          data-testid="hangup-button"
        >
          <i className="fas fa-phone-slash text-sm"></i>
          {hangupMutation.isPending ? 'Encerrando' : 'Encerrar'}
        </Button>
      </div>

      {/* Unified Keypad - Works for both dialing and DTMF */}
      <div className="mt-6 pt-4 border-t border-border">
        <Label className="block text-sm text-muted-foreground mb-3">
          {callState === 'IDLE' ? 'Teclado Numérico - Discagem' : 'Teclado DTMF - Durante Chamada'}
        </Label>
        <div className="grid grid-cols-3 gap-2">
          {dtmfTones.map((tone) => (
            <Button
              key={tone}
              onClick={() => handleKeypadPress(tone)}
              disabled={dtmfMutation.isPending}
              className={`py-3 rounded text-lg font-mono transition-colors ${
                callState === 'IDLE' 
                  ? 'bg-abmix-green text-black border border-abmix-green hover:bg-abmix-green/90' 
                  : callState === 'CONNECTED' 
                    ? 'bg-abmix-green text-black border border-abmix-green hover:bg-abmix-green/90' 
                    : 'bg-muted text-muted-foreground cursor-not-allowed opacity-50'
              }`}
              data-testid={`keypad-${tone}`}
            >
              {tone}
            </Button>
          ))}
        </div>

        {/* Clear and Backspace buttons for dialing */}
        {callState === 'IDLE' && (
          <div className="grid grid-cols-2 gap-2 mt-2">
            <Button
              onClick={() => setPhoneNumber(phoneNumber.slice(0, -1))}
              disabled={phoneNumber.length === 0}
              className="bg-muted text-foreground hover:bg-muted/80 py-2 text-sm"
              data-testid="backspace-button"
            >
              ⌫ Apagar
            </Button>
            <Button
              onClick={() => setPhoneNumber('')}
              disabled={phoneNumber.length === 0}
              className="bg-muted text-foreground hover:bg-muted/80 py-2 text-sm"
              data-testid="clear-button"
            >
              🗑️ Limpar
            </Button>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/DubbingTranslation.content:
import { useState } from 'react';
import { useMutation } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { useToast } from '@/hooks/use-toast';

export function DubbingTranslation() {
  const { toast } = useToast();
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [targetLanguage, setTargetLanguage] = useState('');
  const [preserveVoice, setPreserveVoice] = useState(true);

  const languages = [
    { code: 'pt-BR', name: 'Português (Brasil)' },
    { code: 'en-US', name: 'Inglês (EUA)' },
    { code: 'es-ES', name: 'Espanhol (Espanha)' },
    { code: 'fr-FR', name: 'Francês (França)' },
    { code: 'de-DE', name: 'Alemão (Alemanha)' },
    { code: 'it-IT', name: 'Italiano (Itália)' },
    { code: 'zh-CN', name: 'Chinês (Mandarim)' },
    { code: 'ja-JP', name: 'Japonês' }
  ];

  const dubbingMutation = useMutation({
    mutationFn: async (data: { fileId: string; targetLang: string; preserveVoice: boolean }) => {
      // Implementation will be added when endpoint is ready
      return { success: true, dubbedUrl: '/dubbed-audio.wav' };
    },
    onSuccess: () => {
      toast({
        title: "Dublagem concluída",
        description: "Áudio traduzido e dublado com sucesso",
      });
      setSelectedFile(null);
      setTargetLanguage('');
    },
    onError: (error) => {
      toast({
        title: "Erro na dublagem",
        description: error instanceof Error ? error.message : "Falha no processamento",
        variant: "destructive",
      });
    }
  });

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      if (file.type.startsWith('audio/')) {
        setSelectedFile(file);
      } else {
        toast({
          title: "Arquivo inválido",
          description: "Selecione um arquivo de áudio",
          variant: "destructive",
        });
      }
    }
  };

  const handleStartDubbing = () => {
    if (!selectedFile || !targetLanguage) {
      toast({
        title: "Dados incompletos",
        description: "Selecione um arquivo e idioma de destino",
        variant: "destructive",
      });
      return;
    }

    dubbingMutation.mutate({
      fileId: selectedFile.name,
      targetLang: targetLanguage,
      preserveVoice
    });
  };

  return (
    <div className="bg-dark-surface rounded-xl p-6 border border-dark-border">
      <h3 className="text-lg font-semibold mb-4">Dublagem & Tradução</h3>

      <div className="space-y-4">
        <div>
          <Label htmlFor="dubbing-file" className="text-sm font-medium text-foreground">
            Arquivo de Áudio
          </Label>
          <Input
            id="dubbing-file"
            type="file"
            accept="audio/*"
            onChange={handleFileChange}
            className="mt-1"
            data-testid="dubbing-file-input"
          />
          {selectedFile && (
            <p className="text-xs text-gray-400 mt-1">
              Arquivo: {selectedFile.name} ({Math.round(selectedFile.size / 1024)} KB)
            </p>
          )}
        </div>

        <div>
          <Label className="text-sm font-medium text-foreground">
            Idioma de Destino
          </Label>
          <Select value={targetLanguage} onValueChange={setTargetLanguage}>
            <SelectTrigger className="mt-1" data-testid="language-select">
              <SelectValue placeholder="Selecione o idioma" />
            </SelectTrigger>
            <SelectContent>
              {languages.map((lang) => (
                <SelectItem key={lang.code} value={lang.code}>
                  {lang.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <div className="flex items-center space-x-2">
          <input
            type="checkbox"
            id="preserve-voice"
            checked={preserveVoice}
            onChange={(e) => setPreserveVoice(e.target.checked)}
            className="w-4 h-4 text-abmix-green bg-gray-100 border-gray-300 rounded"
            data-testid="preserve-voice-checkbox"
          />
          <Label htmlFor="preserve-voice" className="text-sm text-foreground">
            Preservar características da voz original
          </Label>
        </div>

        <Button
          onClick={handleStartDubbing}
          disabled={!selectedFile || !targetLanguage || dubbingMutation.isPending}
          className="w-full bg-abmix-green text-black hover:bg-abmix-green/80"
          data-testid="start-dubbing-button"
        >
          {dubbingMutation.isPending ? 'Processando...' : 'Iniciar Dublagem'}
        </Button>

        <div className="mt-4 p-3 bg-dark-bg rounded-lg border border-dark-border">
          <p className="text-xs text-gray-400">
            <strong>Funcionalidade:</strong> Traduz automaticamente o conteúdo de áudio
            para o idioma selecionado, mantendo as características vocais originais.
            Suporta 8+ idiomas principais.
          </p>
        </div>
      </div>
    </div>
  );
}

client/src/components/FavoriteDialog.tsx.content:
import { useState, useEffect } from 'react';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { useToast } from '@/hooks/use-toast';
import { api } from '@/services/api';
import { validateE164, formatPhoneNumber } from '@/utils/validation';
import type { Favorite } from '@shared/schema';

interface FavoriteDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  favorite: Favorite | null;
  onClose: () => void;
}

export function FavoriteDialog({ open, onOpenChange, favorite, onClose }: FavoriteDialogProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [formData, setFormData] = useState({
    name: '',
    number: '',
    tags: '',
    notes: '',
  });

  // Reset form when dialog opens/closes or favorite changes
  useEffect(() => {
    if (open) {
      if (favorite) {
        setFormData({
          name: favorite.name,
          number: favorite.number,
          tags: favorite.tags || '',
          notes: favorite.notes || '',
        });
      } else {
        setFormData({
          name: '',
          number: '',
          tags: '',
          notes: '',
        });
      }
    }
  }, [open, favorite]);

  const createMutation = useMutation({
    mutationFn: (data: typeof formData) => api.createFavorite(data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/favorites'] });
      toast({
        title: "Favorito adicionado",
        description: `${formData.name} foi adicionado aos favoritos`,
      });
      onClose();
    },
    onError: (error) => {
      toast({
        title: "Erro ao adicionar favorito",
        description: error instanceof Error ? error.message : "Falha na operação",
        variant: "destructive",
      });
    }
  });

  const updateMutation = useMutation({
    mutationFn: (data: typeof formData) => api.updateFavorite(favorite!.id, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/favorites'] });
      toast({
        title: "Favorito atualizado",
        description: `${formData.name} foi atualizado`,
      });
      onClose();
    },
    onError: (error) => {
      toast({
        title: "Erro ao atualizar favorito",
        description: error instanceof Error ? error.message : "Falha na operação",
        variant: "destructive",
      });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name.trim()) {
      toast({
        title: "Nome obrigatório",
        description: "Por favor, preencha o nome do favorito",
        variant: "destructive",
      });
      return;
    }

    const formattedNumber = formatPhoneNumber(formData.number);
    if (!validateE164(formattedNumber)) {
      toast({
        title: "Número inválido",
        description: "Por favor, insira um número no formato E.164 (+5511999999999)",
        variant: "destructive",
      });
      return;
    }

    const submitData = {
      ...formData,
      number: formattedNumber,
    };

    if (favorite) {
      updateMutation.mutate(submitData);
    } else {
      createMutation.mutate(submitData);
    }
  };

  const handleInputChange = (field: keyof typeof formData) => (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    setFormData(prev => ({
      ...prev,
      [field]: e.target.value,
    }));
  };

  const isLoading = createMutation.isPending || updateMutation.isPending;

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="bg-dark-surface border-dark-border text-white">
        <DialogHeader>
          <DialogTitle>
            {favorite ? 'Editar Favorito' : 'Adicionar Favorito'}
          </DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label htmlFor="favorite-name">Nome *</Label>
            <Input
              id="favorite-name"
              placeholder="Ex: Suporte Técnico"
              value={formData.name}
              onChange={handleInputChange('name')}
              className="bg-dark-bg border-dark-border"
              data-testid="favorite-name-input"
              required
            />
          </div>

          <div>
            <Label htmlFor="favorite-number">Número *</Label>
            <Input
              id="favorite-number"
              placeholder="+5511999999999"
              value={formData.number}
              onChange={handleInputChange('number')}
              className="bg-dark-bg border-dark-border font-mono"
              data-testid="favorite-number-input"
              required
            />
          </div>

          <div>
            <Label htmlFor="favorite-tags">Tags</Label>
            <Input
              id="favorite-tags"
              placeholder="Ex: trabalho, suporte, vendas"
              value={formData.tags}
              onChange={handleInputChange('tags')}
              className="bg-dark-bg border-dark-border"
              data-testid="favorite-tags-input"
            />
          </div>

          <div>
            <Label htmlFor="favorite-notes">Observações</Label>
            <Textarea
              id="favorite-notes"
              placeholder="Notas adicionais sobre este contato..."
              value={formData.notes}
              onChange={handleInputChange('notes')}
              className="bg-dark-bg border-dark-border h-20 resize-none"
              data-testid="favorite-notes-input"
            />
          </div>

          <div className="flex justify-end space-x-2 pt-4">
            <Button
              type="button"
              variant="outline"
              onClick={onClose}
              className="border-dark-border text-white hover:bg-dark-border"
              data-testid="cancel-favorite-button"
            >
              Cancelar
            </Button>
            <Button
              type="submit"
              disabled={isLoading}
              className="bg-abmix-green text-black hover:bg-abmix-green/90"
              data-testid="save-favorite-button"
            >
              {isLoading ? 'Salvando...' : (favorite ? 'Atualizar' : 'Adicionar')}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/Favorites.tsx.content:
import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { useToast } from '@/hooks/use-toast';
import { useCallStore } from '@/stores/useCallStore';
import { api } from '@/services/api';
import { FavoriteDialog } from '.';
import type { Favorite } from '@shared/schema';

export function Favorites() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { setPhoneNumber } = useCallStore();
  const [editingFavorite, setEditingFavorite] = useState<Favorite | null>(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  const { data: favorites = [], isLoading } = useQuery({
    queryKey: ['/api/favorites'],
    queryFn: () => api.getFavorites(),
  });

  const deleteMutation = useMutation({
    mutationFn: (id: string) => api.deleteFavorite(id),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/favorites'] });
      toast({
        title: "Favorito removido",
      });
    },
    onError: (error) => {
      toast({
        title: "Erro ao remover favorito",
        description: error instanceof Error ? error.message : "Falha na operação",
        variant: "destructive",
      });
    }
  });

  const handleDialFavorite = (favorite: Favorite) => {
    // Set the number in the dialer component
    setPhoneNumber(favorite.number);

    // Also trigger the dialer to update its local state
    const event = new CustomEvent('setDialerNumber', { 
      detail: { number: favorite.number } 
    });
    window.dispatchEvent(event);

    toast({
      title: "Número preenchido",
      description: `${favorite.name}: ${favorite.number}`,
    });
  };

  const handleEditFavorite = (favorite: Favorite) => {
    setEditingFavorite(favorite);
    setIsDialogOpen(true);
  };

  const handleDeleteFavorite = (favorite: Favorite) => {
    if (window.confirm(`Deseja realmente remover ${favorite.name}?`)) {
      deleteMutation.mutate(favorite.id);
    }
  };

  const handleAddFavorite = () => {
    setEditingFavorite(null);
    setIsDialogOpen(true);
  };

  const handleDialogClose = () => {
    setIsDialogOpen(false);
    setEditingFavorite(null);
  };

  if (isLoading) {
    return (
      <div className="bg-dark-surface rounded-xl p-6 border border-dark-border h-fit">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold">Favoritos</h3>
        </div>
        <div className="text-center text-gray-400">Carregando...</div>
      </div>
    );
  }

  return (
    <div className="bg-dark-surface rounded-xl p-6 border border-dark-border h-fit">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold">Favoritos</h3>
        <Button
          onClick={handleAddFavorite}
          className="bg-abmix-green text-black p-2 rounded-lg hover:bg-abmix-green/90 transition-colors"
          data-testid="add-favorite-button"
        >
          <i className="fas fa-plus"></i>
        </Button>
      </div>

      <div className="space-y-3">
        {favorites.length === 0 ? (
          <div className="text-center text-gray-400 py-4">
            <i className="fas fa-star text-2xl mb-2 block"></i>
            Nenhum favorito adicionado
          </div>
        ) : (
          favorites.map((favorite: Favorite) => (
            <div
              key={favorite.id}
              className="bg-dark-bg rounded-lg p-4 border border-dark-border"
              data-testid={`favorite-${favorite.id}`}
            >
              <div className="flex items-center justify-between">
                <div>
                  <h4 className="font-semibold">{favorite.name}</h4>
                  <p className="text-sm text-gray-400 font-mono">{favorite.number}</p>
                  {favorite.notes && (
                    <p className="text-xs text-gray-500 mt-1">{favorite.notes}</p>
                  )}
                </div>
                <div className="flex items-center space-x-2">
                  <Button
                    onClick={() => handleDialFavorite(favorite)}
                    className="text-abmix-green hover:bg-abmix-green/10 p-2 rounded transition-colors"
                    variant="ghost"
                    size="sm"
                    data-testid={`dial-favorite-${favorite.id}`}
                  >
                    <i className="fas fa-phone"></i>
                  </Button>
                  <Button
                    onClick={() => handleEditFavorite(favorite)}
                    className="text-blue-400 hover:bg-blue-400/10 p-2 rounded transition-colors"
                    variant="ghost"
                    size="sm"
                    data-testid={`edit-favorite-${favorite.id}`}
                  >
                    <i className="fas fa-edit"></i>
                  </Button>
                  <Button
                    onClick={() => handleDeleteFavorite(favorite)}
                    disabled={deleteMutation.isPending}
                    className="text-red-400 hover:bg-red-400/10 p-2 rounded transition-colors"
                    variant="ghost"
                    size="sm"
                    data-testid={`delete-favorite-${favorite.id}`}
                  >
                    <i className="fas fa-trash"></i>
                  </Button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      <FavoriteDialog
        open={isDialogOpen}
        onOpenChange={setIsDialogOpen}
        favorite={editingFavorite}
        onClose={handleDialogClose}
      />
    </div>
  );
}

client/src/components/Header.tsx.content:
import { useEffect } from 'react';
import { useCallStore } from '@/stores/useCallStore';
import { metricsService } from '@/services/metrics';

export function Header() {
  const { callState, latency, audioLevel } = useCallStore();
  const setLatency = useCallStore(state => state.setLatency);

  // Connect to real-time metrics
  useEffect(() => {
    metricsService.connect((data) => {
      setLatency(data.latencyMs);
    });

    return () => {
      metricsService.disconnect();
    };
  }, [setLatency]);

  const getStatusConfig = (state: string) => {
    const configs = {
      'IDLE': { color: 'bg-gray-500', text: 'Idle', textClass: '' },
      'RINGING': { color: 'bg-yellow-500', text: 'Chamando...', textClass: 'text-yellow-400' },
      'CONNECTED': { color: 'bg-abmix-green', text: 'Conectado', textClass: 'text-abmix-green' },
      'ENDED': { color: 'bg-red-500', text: 'Encerrada', textClass: 'text-red-400' }
    };
    return configs[state as keyof typeof configs] || configs.IDLE;
  };

  const statusConfig = getStatusConfig(callState);

  return (
    <header className="bg-dark-surface border-b border-dark-border px-6 py-4">
      <div className="flex items-center justify-between">
        {/* Call Status */}
        <div className="flex items-center space-x-6">
          <div className="flex items-center space-x-3">
            <div className={`w-3 h-3 ${statusConfig.color} rounded-full`} data-testid="status-indicator"></div>
            <span className={`text-lg font-semibold ${statusConfig.textClass}`} data-testid="call-state">
              {statusConfig.text}
            </span>
          </div>
          <div className="text-sm text-gray-400">
            Latência: <span className="text-abmix-green" data-testid="latency-display">
              {latency > 0 ? `${Math.round(latency)} ms` : '-- ms'}
            </span>
          </div>
        </div>

        {/* Audio Indicator */}
        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-2">
            <i className="fas fa-microphone text-gray-400"></i>
            <div className="w-16 h-2 bg-dark-border rounded-full overflow-hidden">
              <div 
                className="h-full bg-abmix-green transition-all duration-150" 
                style={{ width: `${audioLevel}%` }}
                data-testid="audio-level"
              ></div>
            </div>
          </div>
          <button className="p-2 hover:bg-dark-border rounded-lg transition-colors" data-testid="volume-button">
            <i className="fas fa-volume-up text-gray-400"></i>
          </button>
        </div>
      </div>
    </header>
  );
}

