import { Button } from '@/components/ui/button';
import { useToast } from '@/hooks/use-toast';
import { useCallStore } from '@/stores/useCallStore';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useState, useEffect, useRef } from 'react';

interface Recording {
  id: number;
  call_sid: string;
  phone_number: string;
  filename: string;
  duration_sec: number;
  started_at: string;
  ended_at: string;
  size_bytes: number;
}

export function Recordings() {
  const { toast } = useToast();
  const { isRecording, setIsRecording, currentCallId } = useCallStore();
  const [recordingStartTime, setRecordingStartTime] = useState<number | null>(null);
  const [currentRecordingId, setCurrentRecordingId] = useState<number | null>(null);
  const [mediaRecorder, setMediaRecorder] = useState<MediaRecorder | null>(null);
  const [audioStream, setAudioStream] = useState<MediaStream | null>(null);
  const audioChunks = useRef<Blob[]>([]);
  const queryClient = useQueryClient();

  // Fetch recordings list
  const { data: recordings = [], isLoading } = useQuery({
    queryKey: ['/api/recordings/list'],
    queryFn: async () => {
      const response = await fetch('/api/recordings/list');
      if (!response.ok) throw new Error('Failed to fetch recordings');
      return response.json();
    },
    refetchInterval: isRecording ? 5000 : false, // Refresh while recording
  });

  // Start recording mutation
  const startRecordingMutation = useMutation({
    mutationFn: async (phoneNumber: string) => {
      const response = await fetch('/api/recordings/start', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          callSid: currentCallId || `call-${Date.now()}`,
          phoneNumber: phoneNumber
        })
      });
      if (!response.ok) throw new Error('Failed to start recording');
      return response.json();
    },
    onSuccess: (data) => {
      setCurrentRecordingId(data.id);
      setRecordingStartTime(Date.now());
      setIsRecording(true);
      queryClient.invalidateQueries({ queryKey: ['/api/recordings/list'] });
    }
  });

  // Stop recording mutation
  const stopRecordingMutation = useMutation({
    mutationFn: async () => {
      if (!currentRecordingId) throw new Error('No active recording');
      const response = await fetch(`/api/recordings/${currentRecordingId}/stop`, {
        method: 'POST',
      });
      if (!response.ok) throw new Error('Failed to stop recording');
      return response.json();
    },
    onSuccess: () => {
      setIsRecording(false);
      setRecordingStartTime(null);
      setCurrentRecordingId(null);
      queryClient.invalidateQueries({ queryKey: ['/api/recordings/list'] });
    }
  });

  // Format duration helper
  const formatDuration = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // Format file size helper
  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  const handleStartRecording = async () => {
    try {
      if (currentCallId) {
        // Recording during a call - use Twilio recording
        const phoneNumber = "11999887766";
        startRecordingMutation.mutate(phoneNumber);
        toast({
          title: "Gravação de ligação iniciada",
          description: `Gravando chamada para ${phoneNumber}`,
        });
      } else {
        // Recording from microphone when not in call
        const stream = await navigator.mediaDevices.getUserMedia({ 
          audio: {
            echoCancellation: true,
            noiseSuppression: true,
            sampleRate: 44100
          } 
        });

        setAudioStream(stream);
        audioChunks.current = [];

        const recorder = new MediaRecorder(stream, {
          mimeType: 'audio/webm;codecs=opus'
        });

        recorder.ondataavailable = (event) => {
          if (event.data.size > 0) {
            audioChunks.current.push(event.data);
          }
        };

        recorder.onstop = async () => {
          const audioBlob = new Blob(audioChunks.current, { type: 'audio/webm' });
          await uploadMicrophoneRecording(audioBlob);
        };

        recorder.start(1000); // Collect data every second
        setMediaRecorder(recorder);
        setIsRecording(true);
        setRecordingStartTime(Date.now());

        toast({
          title: "Gravação do microfone iniciada",
          description: "Gravando áudio do microfone",
        });
      }
    } catch (error) {
      console.error('Error starting recording:', error);
      toast({
        title: "Erro ao iniciar gravação",
        description: "Não foi possível acessar o microfone",
        variant: "destructive",
      });
    }
  };

  const pauseRecordingMutation = useMutation({
    mutationFn: async () => {
      if (!currentRecordingId) throw new Error('No active recording');
      const response = await fetch(`/api/recordings/${currentRecordingId}/pause`, {
        method: 'POST',
      });
      if (!response.ok) throw new Error('Failed to pause recording');
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Gravação pausada",
        description: "Gravação pausada temporariamente",
      });
    },
    onError: (error) => {
      toast({
        title: "Erro ao pausar",
        description: error instanceof Error ? error.message : "Falha na operação",
        variant: "destructive",
      });
    }
  });

  const resumeRecordingMutation = useMutation({
    mutationFn: async () => {
      if (!currentRecordingId) throw new Error('No active recording');
      const response = await fetch(`/api/recordings/${currentRecordingId}/resume`, {
        method: 'POST',
      });
      if (!response.ok) throw new Error('Failed to resume recording');
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Gravação retomada",
        description: "Gravação retomada com sucesso",
      });
    },
    onError: (error) => {
      toast({
        title: "Erro ao retomar",
        description: error instanceof Error ? error.message : "Falha na operação",
        variant: "destructive",
      });
    }
  });

  const handlePauseRecording = () => {
    if (mediaRecorder && mediaRecorder.state === 'recording') {
      // Pause microphone recording
      mediaRecorder.pause();
      toast({
        title: "Gravação pausada",
        description: "Microfone pausado temporariamente",
      });
    } else if (currentRecordingId) {
      // Pause Twilio call recording
      pauseRecordingMutation.mutate();
    } else {
      toast({
        title: "Nenhuma gravação ativa",
        variant: "destructive",
      });
    }
  };

  const handleResumeRecording = () => {
    if (mediaRecorder && mediaRecorder.state === 'paused') {
      // Resume microphone recording
      mediaRecorder.resume();
      toast({
        title: "Gravação retomada",
        description: "Microfone retomado",
      });
    } else if (currentRecordingId) {
      // Resume Twilio call recording
      resumeRecordingMutation.mutate();
    } else {
      toast({
        title: "Nenhuma gravação pausada",
        variant: "destructive",
      });
    }
  };

  const handleStopRecording = () => {
    if (currentRecordingId && currentCallId) {
      // Stop Twilio call recording
      stopRecordingMutation.mutate();
    } else if (mediaRecorder && mediaRecorder.state === 'recording') {
      // Stop microphone recording
      mediaRecorder.stop();
      if (audioStream) {
        audioStream.getTracks().forEach(track => track.stop());
        setAudioStream(null);
      }
      setMediaRecorder(null);
      setIsRecording(false);
      setRecordingStartTime(null);

      toast({
        title: "Gravação finalizada",
        description: "Processando áudio do microfone...",
      });
    }
  };

  const uploadMicrophoneRecording = async (audioBlob: Blob) => {
    try {
      const formData = new FormData();
      const timestamp = Date.now();
      const filename = `microfone-${timestamp}.webm`;

      formData.append('audio', audioBlob, filename);
      formData.append('phoneNumber', 'Microfone');
      formData.append('callSid', `mic-${timestamp}`);

      const response = await fetch('/api/recordings/upload', {
        method: 'POST',
        body: formData
      });

      if (response.ok) {
        queryClient.invalidateQueries({ queryKey: ['/api/recordings/list'] });
        toast({
          title: "Gravação salva",
          description: "Áudio do microfone salvo com sucesso",
        });
      } else {
        throw new Error('Upload failed');
      }
    } catch (error) {
      console.error('Error uploading recording:', error);
      toast({
        title: "Erro ao salvar",
        description: "Não foi possível salvar a gravação",
        variant: "destructive",
      });
    }
  };

  const handleDownload = async (recording: Recording) => {
    try {
      const response = await fetch(`/api/recordings/${recording.id}/download`);
      if (!response.ok) throw new Error('Failed to download');

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = recording.filename;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

      toast({
        title: "Download iniciado",
        description: `Baixando ${recording.filename}`,
      });
    } catch (error) {
      toast({
        title: "Erro no download",
        description: "Não foi possível baixar a gravação",
      });
    }
  };

  // Delete recording mutation
  const deleteRecordingMutation = useMutation({
    mutationFn: async (recordingId: number) => {
      const response = await fetch(`/api/recordings/${recordingId}`, {
        method: 'DELETE',
      });
      if (!response.ok) throw new Error('Failed to delete recording');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/recordings/list'] });
      toast({
        title: "Gravação excluída",
        description: "Arquivo removido com sucesso",
      });
    },
    onError: () => {
      toast({
        title: "Erro ao excluir",
        description: "Não foi possível excluir a gravação",
        variant: "destructive",
      });
    }
  });

  const handleDelete = async (recording: Recording) => {
    deleteRecordingMutation.mutate(recording.id);
  };

  return (
    <div className="bg-card rounded-xl p-6 border border-border h-fit">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-foreground">Gravações</h3>
        <div className="flex items-center space-x-2">
          <div 
            className={`w-3 h-3 rounded-full transition-all duration-300 ${
              isRecording ? 'bg-red-500 animate-pulse' : 'bg-gray-400 opacity-30'
            }`}
            data-testid="recording-indicator"
          ></div>
          <span className="text-sm text-muted-foreground">
            {isRecording ? 'Gravando' : 'Inativo'}
          </span>
        </div>
      </div>

      <div className="space-y-4">
        {/* Control Buttons */}
        <div className="grid grid-cols-2 gap-2">
          {!isRecording ? (
            <Button
              onClick={handleStartRecording}
              disabled={startRecordingMutation.isPending}
              className="bg-abmix-green text-black py-2 px-2 rounded-lg hover:bg-abmix-green/90 transition-colors text-xs font-medium disabled:opacity-50 flex flex-col items-center justify-center gap-1"
              data-testid="start-recording-button"
            >
              <i className="fas fa-record-vinyl text-sm"></i>
              {startRecordingMutation.isPending ? 'Iniciando...' : 'Iniciar'}
            </Button>
          ) : (
            <Button
              onClick={handlePauseRecording}
              disabled={stopRecordingMutation.isPending}
              className="bg-abmix-green text-black py-2 px-2 rounded-lg hover:bg-abmix-green/90 transition-colors text-xs font-medium disabled:opacity-50 flex flex-col items-center justify-center gap-1"
              data-testid="pause-recording-button"
            >
              <i className="fas fa-pause text-sm"></i>
              Pausar
            </Button>
          )}

          <Button
            onClick={handleStopRecording}
            disabled={!isRecording || stopRecordingMutation.isPending}
            className="bg-abmix-green text-black py-2 px-2 rounded-lg hover:bg-abmix-green/90 transition-colors text-xs font-medium disabled:opacity-50 flex flex-col items-center justify-center gap-1"
            data-testid="stop-recording-button"
          >
            <i className="fas fa-stop text-sm"></i>
            {stopRecordingMutation.isPending ? 'Parando...' : 'Parar'}
          </Button>
        </div>

        {/* Recordings List */}
        <div className="max-h-80 overflow-y-auto space-y-2">
          {isLoading ? (
            <div className="text-center py-4 text-muted-foreground">
              <i className="fas fa-spinner fa-spin mr-2"></i>
              Carregando gravações...
            </div>
          ) : recordings.length === 0 ? (
            <div className="text-center py-4 text-muted-foreground">
              <i className="fas fa-microphone-slash mr-2"></i>
              Nenhuma gravação encontrada
            </div>
          ) : (
            recordings.map((recording: Recording) => (
              <div key={recording.id} className="bg-background rounded-lg p-3 border border-border">
                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <div className="text-sm font-medium text-foreground">
                      {recording.phone_number || 'Número não informado'}
                    </div>
                    <div className="text-xs text-muted-foreground mt-1">
                      {formatDuration(recording.duration_sec)} • {formatFileSize(recording.size_bytes)}
                    </div>
                    <div className="text-xs text-muted-foreground">
                      {new Date(recording.started_at).toLocaleString('pt-BR')}
                    </div>
                  </div>
                  <div className="flex gap-1 ml-2">
                    <Button
                      onClick={() => handleDownload(recording)}
                      size="sm"
                      variant="outline"
                      className="p-1 h-8 w-8 border-border text-foreground hover:bg-muted"
                      data-testid={`download-button-${recording.id}`}
                    >
                      <i className="fas fa-download text-xs"></i>
                    </Button>
                    <Button
                      onClick={() => handleDelete(recording)}
                      size="sm"
                      variant="outline"
                      disabled={deleteRecordingMutation.isPending}
                      className="p-1 h-8 w-8 border-abmix-green text-abmix-green hover:bg-abmix-green hover:text-black"
                      data-testid={`delete-button-${recording.id}`}
                    >
                      <i className="fas fa-trash text-xs"></i>
                    </Button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

client/src/components/RecordingsTab.tsx.content:
import { Button } from '@/components/ui/button';
import { useToast } from '@/hooks/use-toast';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useState } from 'react';

interface Recording {
  id: number;
  call_sid: string;
  phone_number: string;
  filename: string;
  duration_sec: number;
  started_at: string;
  ended_at: string;
  size_bytes: number;
}

export function RecordingsTab() {
  const { toast } = useToast();
  const [playingId, setPlayingId] = useState<number | null>(null);
  const queryClient = useQueryClient();

  // Fetch recordings list
  const { data: recordings = [], isLoading } = useQuery({
    queryKey: ['/api/recordings/list'],
    queryFn: async () => {
      const response = await fetch('/api/recordings/list');
      if (!response.ok) throw new Error('Failed to fetch recordings');
      return response.json();
    }
  });

  // Format duration helper
  const formatDuration = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // Format file size helper
  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  const handlePlay = async (recordingId: number) => {
    if (playingId === recordingId) {
      // Stop current playback
      const audioElements = document.querySelectorAll('audio');
      audioElements.forEach(audio => {
        audio.pause();
        audio.currentTime = 0;
      });
      setPlayingId(null);
      toast({
        title: "Reprodução pausada",
        description: "Áudio pausado",
      });
    } else {
      try {
        // Stop any other playing audio first
        const audioElements = document.querySelectorAll('audio');
        audioElements.forEach(audio => {
          audio.pause();
          audio.currentTime = 0;
        });

        // Create and configure new audio
        const audio = new Audio(`/api/recordings/${recordingId}/play`);
        audio.crossOrigin = "anonymous";
        audio.preload = "auto";

        setPlayingId(recordingId);

        audio.onended = () => {
          setPlayingId(null);
          toast({
            title: "Reprodução finalizada",
            description: "Áudio reproduzido completamente",
          });
        };

        audio.onerror = (e) => {
          console.error('Audio playback error:', e);
          setPlayingId(null);
          toast({
            title: "Erro na reprodução",
            description: "Não foi possível carregar o áudio",
            variant: "destructive",
          });
        };

        audio.onloadstart = () => {
          console.log('Audio loading started for recording:', recordingId);
        };

        audio.oncanplay = () => {
          console.log('Audio can start playing for recording:', recordingId);
        };

        // Start loading and playing
        await audio.play();
        toast({
          title: "Reproduzindo áudio",
          description: "Tocando gravação da ligação",
        });
      } catch (error) {
        console.error('Play error:', error);
        setPlayingId(null);
        toast({
          title: "Erro na reprodução", 
          description: "Não foi possível reproduzir o áudio",
          variant: "destructive",
        });
      }
    }
  };

  const handleStop = (recordingId: number) => {
    // Stop all audio elements
    const audioElements = document.querySelectorAll('audio');
    audioElements.forEach(audio => {
      audio.pause();
      audio.currentTime = 0;
    });
    setPlayingId(null);
    toast({
      title: "Reprodução parada",
      description: "Áudio parado",
    });
  };

  const handleDownload = async (recording: Recording) => {
    try {
      const response = await fetch(`/api/recordings/${recording.id}/download`);
      if (!response.ok) throw new Error('Failed to download');

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = recording.filename;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

      toast({
        title: "Download iniciado",
        description: `Baixando ${recording.filename}`,
      });
    } catch (error) {
      toast({
        title: "Erro no download",
        description: "Não foi possível baixar a gravação",
      });
    }
  };

  // Delete recording mutation
  const deleteRecordingMutation = useMutation({
    mutationFn: async (recordingId: number) => {
      const response = await fetch(`/api/recordings/${recordingId}`, {
        method: 'DELETE',
      });
      if (!response.ok) throw new Error('Failed to delete recording');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/recordings/list'] });
      toast({
        title: "Gravação excluída",
        description: "Arquivo removido com sucesso",
      });
    },
    onError: () => {
      toast({
        title: "Erro ao excluir",
        description: "Não foi possível excluir a gravação",
        variant: "destructive",
      });
    }
  });

  const handleDelete = async (recording: Recording) => {
    deleteRecordingMutation.mutate(recording.id);
  };

  return (
    <div className="p-6">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-2xl font-bold text-foreground">Gravações</h2>
        <div className="text-sm text-muted-foreground">
          {recordings.length} arquivo{recordings.length !== 1 ? 's' : ''}
        </div>
      </div>

      <div className="space-y-4">
        {isLoading ? (
          <div className="text-center py-8 text-muted-foreground">
            <i className="fas fa-spinner fa-spin mr-2"></i>
            Carregando gravações...
          </div>
        ) : recordings.length === 0 ? (
          <div className="text-center py-8 text-muted-foreground">
            <i className="fas fa-microphone-slash text-3xl mb-3"></i>
            <p>Nenhuma gravação encontrada</p>
            <p className="text-sm">Inicie uma gravação no painel principal</p>
          </div>
        ) : (
          recordings.map((recording: Recording) => (
            <div key={recording.id} className="bg-card rounded-lg p-4 border border-border">
              <div className="flex items-center justify-between">
                <div className="flex-1">
                  <div className="flex items-center space-x-3">
                    <div className="w-10 h-10 bg-abmix-green/20 rounded-full flex items-center justify-center">
                      <i className="fas fa-phone text-abmix-green"></i>
                    </div>
                    <div>
                      <div className="text-lg font-medium text-foreground">
                        {recording.phone_number || 'Número não informado'}
                      </div>
                      <div className="text-sm text-muted-foreground">
                        {formatDuration(recording.duration_sec)} • {formatFileSize(recording.size_bytes)}
                      </div>
                      <div className="text-xs text-muted-foreground">
                        {new Date(recording.started_at).toLocaleString('pt-BR')}
                      </div>
                    </div>
                  </div>
                </div>

                <div className="flex gap-2 ml-4">
                  <Button
                    onClick={() => handlePlay(recording.id)}
                    size="sm"
                    variant="outline"
                    className="p-2 h-9 w-9 border-border text-foreground hover:bg-muted"
                    data-testid={`play-button-${recording.id}`}
                  >
                    <i className={`fas ${playingId === recording.id ? 'fa-pause' : 'fa-play'} text-sm`}></i>
                  </Button>

                  <Button
                    onClick={() => handleStop(recording.id)}
                    size="sm"
                    variant="outline"
                    disabled={playingId !== recording.id}
                    className="p-2 h-9 w-9 border-border text-foreground hover:bg-muted disabled:opacity-50"
                    data-testid={`stop-button-${recording.id}`}
                  >
                    <i className="fas fa-stop text-sm"></i>
                  </Button>

                  <Button
                    onClick={() => handleDownload(recording)}
                    size="sm"
                    variant="outline"
                    className="p-2 h-9 w-9 border-border text-foreground hover:bg-muted"
                    data-testid={`download-button-${recording.id}`}
                  >
                    <i className="fas fa-download text-sm"></i>
                  </Button>

                  <Button
                    onClick={() => handleDelete(recording)}
                    size="sm"
                    variant="outline"
                    disabled={deleteRecordingMutation.isPending}
                    className="p-2 h-9 w-9 border-abmix-green text-abmix-green hover:bg-abmix-green hover:text-black"
                    data-testid={`delete-button-${recording.id}`}
                  >
                    <i className="fas fa-trash text-sm"></i>
                  </Button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

client/src/components/Settings.tsx.content:
import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import { useToast } from '@/hooks/use-toast';
import { useCallStore } from '@/stores/useCallStore';
import { useTheme } from '@/contexts/ThemeContext';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';

export function Settings() {
  const { toast } = useToast();
  const { selectedProvider, setSelectedProvider } = useCallStore();
  const { theme, setTheme } = useTheme();

  const [settings, setSettings] = useState({
    autoRecord: false,
    defaultCountryCode: '+55',
    voiceProvider: 'vapi',
    aiModel: 'gpt-4',
    language: 'pt-BR',
    notifications: true,
    volume: 80,
    mascVoiceId: 'pNInz6obpgDQGcFmaJgB', // Default male voice
    femVoiceId: '21m00Tcm4TlvDq8ikWAM', // Default female voice
  });

  // Fetch available voices from ElevenLabs
  const { data: voices, isLoading: voicesLoading } = useQuery({
    queryKey: ['/api/voices'],
    queryFn: async () => {
      const response = await fetch('/api/voices');
      if (!response.ok) throw new Error('Failed to fetch voices');
      return response.json();
    }
  });

  // Voice categorization
  const maleVoices = ['Roger', 'Clyde', 'Thomas', 'Charlie'];
  const femaleVoices = ['Sarah', 'Rachel', 'Laura', 'Aria'];

  // Filter voices by category
  const masculineVoices = voices?.filter((voice: any) => 
    maleVoices.some(name => voice.name.includes(name))
  ) || [];

  const feminineVoices = voices?.filter((voice: any) => 
    femaleVoices.some(name => voice.name.includes(name))
  ) || [];

  // Fetch current settings
  const { data: serverSettings } = useQuery({
    queryKey: ['/api/settings'],
    queryFn: async () => {
      const response = await fetch('/api/settings');
      if (!response.ok) throw new Error('Failed to fetch settings');
      return response.json();
    }
  });

  // Update settings mutation
  const queryClient = useQueryClient();
  const updateSettingsMutation = useMutation({
    mutationFn: async (newSettings: any) => {
      const response = await fetch('/api/settings', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newSettings)
      });
      if (!response.ok) throw new Error('Failed to update settings');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/settings'] });
      toast({
        title: "Configurações salvas",
        description: "Configurações de voz atualizadas com sucesso",
      });
    }
  });

  // Update local settings when server settings load
  useEffect(() => {
    if (serverSettings) {
      setSettings(prev => ({
        ...prev,
        mascVoiceId: serverSettings.VOZ_MASC_ID || prev.mascVoiceId,
        femVoiceId: serverSettings.VOZ_FEM_ID || prev.femVoiceId,
      }));
    }
  }, [serverSettings]);

  const handleSettingChange = (key: string, value: any) => {
    setSettings(prev => ({
      ...prev,
      [key]: value
    }));

    toast({
      title: "Configuração salva",
      description: `${key} atualizada`,
    });
  };

  const handleProviderChange = (provider: string) => {
    setSelectedProvider(provider as any);
    handleSettingChange('voiceProvider', provider);
  };

  const handleThemeChange = (newTheme: 'light' | 'dark') => {
    setTheme(newTheme);
    toast({
      title: "Tema alterado",
      description: `Modo ${newTheme === 'dark' ? 'escuro' : 'claro'} ativado`,
    });
  };

  const resetSettings = () => {
    setSettings({
      autoRecord: false,
      defaultCountryCode: '+55',
      voiceProvider: 'vapi',
      aiModel: 'gpt-4',
      language: 'pt-BR',
      notifications: true,
      volume: 80,
      mascVoiceId: 'pNInz6obpgDQGcFmaJgB',
      femVoiceId: '21m00Tcm4TlvDq8ikWAM',
    });
    setTheme('dark');

    toast({
      title: "Configurações resetadas",
      description: "Todas as configurações foram restauradas para o padrão",
    });
  };

  return (
    <div className="bg-card rounded-xl p-6 border border-border">
      <h3 className="text-lg font-semibold mb-6 text-foreground">Configurações</h3>

      <div className="space-y-6">
        {/* Provedor de Voz */}
        <div>
          <Label className="text-sm font-medium mb-2 block">Provedor de Voz</Label>
          <div className="bg-background border border-border rounded-lg px-3 py-2">
            <span className="text-foreground capitalize">{selectedProvider}</span>
          </div>
          <div className="flex gap-2 mt-2">
            <Button
              onClick={() => handleProviderChange('vapi')}
              variant={selectedProvider === 'vapi' ? 'default' : 'outline'}
              size="sm"
              className={selectedProvider === 'vapi' ? 'bg-abmix-green text-black' : 'border-border text-foreground'}
            >
              Vapi
            </Button>
            <Button
              onClick={() => handleProviderChange('retell')}
              variant={selectedProvider === 'retell' ? 'default' : 'outline'}
              size="sm"
              className={selectedProvider === 'retell' ? 'bg-abmix-green text-black' : 'border-border text-foreground'}
            >
              Retell AI
            </Button>
            <Button
              onClick={() => handleProviderChange('twilio')}
              variant={selectedProvider === 'twilio' ? 'default' : 'outline'}
              size="sm"
              className={selectedProvider === 'twilio' ? 'bg-abmix-green text-black' : 'border-border text-foreground'}
            >
              Twilio
            </Button>
          </div>
        </div>

        {/* Modelo de IA */}
        <div>
          <Label className="text-sm font-medium mb-2 block">Modelo de IA</Label>
          <div className="bg-background border border-border rounded-lg px-3 py-2">
            <span className="text-foreground">{settings.aiModel}</span>
          </div>
          <div className="flex gap-2 mt-2">
            <Button
              onClick={() => handleSettingChange('aiModel', 'gpt-4')}
              variant={settings.aiModel === 'gpt-4' ? 'default' : 'outline'}
              size="sm"
              className={settings.aiModel === 'gpt-4' ? 'bg-abmix-green text-black' : 'border-border text-foreground'}
            >
              GPT-4
            </Button>
            <Button
              onClick={() => handleSettingChange('aiModel', 'gpt-3.5-turbo')}
              variant={settings.aiModel === 'gpt-3.5-turbo' ? 'default' : 'outline'}
              size="sm"
              className={settings.aiModel === 'gpt-3.5-turbo' ? 'bg-abmix-green text-black' : 'border-border text-foreground'}
            >
              GPT-3.5
            </Button>
          </div>
        </div>

        {/* Configuração de Vozes ElevenLabs */}
        <div className="space-y-4 p-4 bg-muted/30 rounded-lg border border-border">
          <div className="flex items-center gap-2 mb-3">
            <i className="fas fa-microphone text-abmix-green"></i>
            <Label className="text-sm font-medium">Configuração de Vozes ElevenLabs</Label>
          </div>

          {/* Voz Masculina */}
          <div>
            <Label className="text-sm font-medium mb-2 block flex items-center gap-2">
              <i className="fas fa-male text-blue-500"></i>
              Voz Masculina
            </Label>
            <div className="bg-background border border-border rounded-lg px-3 py-2">
              <span className="text-foreground text-sm">
                {voicesLoading ? 'Carregando vozes...' : 
                 voices?.find((v: any) => v.voice_id === settings.mascVoiceId)?.name || 'Não selecionada'}
              </span>
            </div>
            <div className="flex gap-2 mt-2 flex-wrap">
              {masculineVoices.map((voice: any) => (
                <Button
                  key={voice.voice_id}
                  onClick={() => {
                    handleSettingChange('mascVoiceId', voice.voice_id);
                    updateSettingsMutation.mutate({ VOZ_MASC_ID: voice.voice_id });
                  }}
                  variant={settings.mascVoiceId === voice.voice_id ? 'default' : 'outline'}
                  size="sm"
                  className={settings.mascVoiceId === voice.voice_id ? 'bg-abmix-green text-black' : 'border-border text-foreground text-xs'}
                >
                  {voice.name}
                </Button>
              ))}
            </div>
          </div>

          {/* Voz Feminina */}
          <div>
            <Label className="text-sm font-medium mb-2 block flex items-center gap-2">
              <i className="fas fa-female text-pink-500"></i>
              Voz Feminina
            </Label>
            <div className="bg-background border border-border rounded-lg px-3 py-2">
              <span className="text-foreground text-sm">
                {voicesLoading ? 'Carregando vozes...' : 
                 voices?.find((v: any) => v.voice_id === settings.femVoiceId)?.name || 'Não selecionada'}
              </span>
            </div>
            <div className="flex gap-2 mt-2 flex-wrap">
              {feminineVoices.map((voice: any) => (
                <Button
                  key={voice.voice_id}
                  onClick={() => {
                    handleSettingChange('femVoiceId', voice.voice_id);
                    updateSettingsMutation.mutate({ VOZ_FEM_ID: voice.voice_id });
                  }}
                  variant={settings.femVoiceId === voice.voice_id ? 'default' : 'outline'}
                  size="sm"
                  className={settings.femVoiceId === voice.voice_id ? 'bg-abmix-green text-black' : 'border-border text-foreground text-xs'}
                >
                  {voice.name}
                </Button>
              ))}
            </div>
          </div>

          {/* Informações das Vozes Selecionadas */}
          <div className="bg-background/50 rounded-lg p-3 text-xs text-muted-foreground">
            <div className="flex justify-between items-center">
              <span>Vozes configuradas para o discador:</span>
              <div className="flex gap-2">
                <span className="text-blue-500">♂ {voices?.find((v: any) => v.voice_id === settings.mascVoiceId)?.name || 'Padrão'}</span>
                <span className="text-pink-500">♀ {voices?.find((v: any) => v.voice_id === settings.femVoiceId)?.name || 'Padrão'}</span>
              </div>
            </div>
          </div>

          {/* Teste de Vozes */}
          <div className="flex gap-2 pt-2">
            <Button
              size="sm"
              variant="outline"
              className="flex-1 border-border text-foreground hover:bg-muted"
              onClick={async () => {
                const selectedVoice = voices?.find((v: any) => v.voice_id === settings.mascVoiceId);
                if (!selectedVoice) {
                  toast({
                    title: "Erro",
                    description: "Nenhuma voz masculina selecionada",
                  });
                  return;
                }

                try {
                  const response = await fetch('/api/voices/test', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                      voiceId: settings.mascVoiceId,
                      text: 'Olá, bem-vindo à Disck Mix. Seu painel inteligente de ligações.'
                    })
                  });

                  if (response.ok) {
                    const audioBlob = await response.blob();
                    const audioUrl = URL.createObjectURL(audioBlob);
                    const audio = new Audio(audioUrl);
                    await audio.play();

                    toast({
                      title: "Teste de voz masculina",
                      description: `Reproduzindo: ${selectedVoice.name}`,
                    });
                  } else {
                    throw new Error('Erro no teste de voz');
                  }
                } catch (error) {
                  toast({
                    title: "Erro no teste",
                    description: "Não foi possível reproduzir a voz masculina",
                  });
                }
              }}
            >
              <i className="fas fa-play mr-2"></i>
              Testar Masc
            </Button>
            <Button
              size="sm"
              variant="outline"
              className="flex-1 border-border text-foreground hover:bg-muted"
              onClick={async () => {
                const selectedVoice = voices?.find((v: any) => v.voice_id === settings.femVoiceId);
                if (!selectedVoice) {
                  toast({
                    title: "Erro",
                    description: "Nenhuma voz feminina selecionada",
                  });
                  return;
                }

                try {
                  const response = await fetch('/api/voices/test', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                      voiceId: settings.femVoiceId,
                      text: 'Olá, bem-vindo à Disck Mix. Seu painel inteligente de ligações.'
                    })
                  });

                  if (response.ok) {
                    const audioBlob = await response.blob();
                    const audioUrl = URL.createObjectURL(audioBlob);
                    const audio = new Audio(audioUrl);
                    await audio.play();

                    toast({
                      title: "Teste de voz feminina",
                      description: `Reproduzindo: ${selectedVoice.name}`,
                    });
                  } else {
                    throw new Error('Erro no teste de voz');
                  }
                } catch (error) {
                  toast({
                    title: "Erro no teste",
                    description: "Não foi possível reproduzir a voz feminina",
                  });
                }
              }}
            >
              <i className="fas fa-play mr-2"></i>
              Testar Fem
            </Button>
          </div>
        </div>

        {/* Idioma */}
        <div>
          <Label className="text-sm font-medium mb-2 block">Idioma</Label>
          <div className="bg-background border border-border rounded-lg px-3 py-2">
            <span className="text-foreground">
              {settings.language === 'pt-BR' ? 'Português (Brasil)' : 
               settings.language === 'en-US' ? 'English (US)' : 'Español'}
            </span>
          </div>
          <div className="flex gap-2 mt-2">
            <Button
              onClick={() => handleSettingChange('language', 'pt-BR')}
              variant={settings.language === 'pt-BR' ? 'default' : 'outline'}
              size="sm"
              className={settings.language === 'pt-BR' ? 'bg-abmix-green text-black' : 'border-border text-foreground'}
            >
              PT-BR
            </Button>
            <Button
              onClick={() => handleSettingChange('language', 'en-US')}
              variant={settings.language === 'en-US' ? 'default' : 'outline'}
              size="sm"
              className={settings.language === 'en-US' ? 'bg-abmix-green text-black' : 'border-border text-foreground'}
            >
              EN-US
            </Button>
          </div>
        </div>

        {/* Código do País Padrão */}
        <div>
          <Label htmlFor="country-code" className="text-sm font-medium mb-2 block">
            Código do País Padrão
          </Label>
          <Input
            id="country-code"
            value={settings.defaultCountryCode}
            onChange={(e) => handleSettingChange('defaultCountryCode', e.target.value)}
            placeholder="+55"
            className="bg-background border-border"
          />
        </div>

        {/* Volume */}
        <div>
          <Label htmlFor="volume" className="text-sm font-medium mb-2 block">
            Volume ({settings.volume}%)
          </Label>
          <Input
            id="volume"
            type="range"
            min="0"
            max="100"
            value={settings.volume}
            onChange={(e) => handleSettingChange('volume', parseInt(e.target.value))}
            className="bg-background border-border"
          />
        </div>

        {/* Switches */}
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <Label htmlFor="auto-record" className="text-sm font-medium">
              Gravação Automática
            </Label>
            <Switch
              id="auto-record"
              checked={settings.autoRecord}
              onCheckedChange={(checked) => handleSettingChange('autoRecord', checked)}
            />
          </div>

          <div className="flex items-center justify-between">
            <Label htmlFor="notifications" className="text-sm font-medium">
              Notificações
            </Label>
            <Switch
              id="notifications"
              checked={settings.notifications}
              onCheckedChange={(checked) => handleSettingChange('notifications', checked)}
            />
          </div>

          <div>
            <Label className="text-sm font-medium mb-2 block">Tema</Label>
            <div className="bg-card border border-border rounded-lg px-3 py-2">
              <span className="text-foreground capitalize">
                {theme === 'dark' ? 'Modo Escuro' : 'Modo Claro'}
              </span>
            </div>
            <div className="flex gap-2 mt-2">
              <Button
                onClick={() => handleThemeChange('light')}
                variant={theme === 'light' ? 'default' : 'outline'}
                size="sm"
                className={theme === 'light' ? 'bg-abmix-green text-black' : 'border-border text-foreground'}
                data-testid="light-mode-button"
              >
                <i className="fas fa-sun text-sm mr-1"></i>
                Claro
              </Button>
              <Button
                onClick={() => handleThemeChange('dark')}
                variant={theme === 'dark' ? 'default' : 'outline'}
                size="sm"
                className={theme === 'dark' ? 'bg-abmix-green text-black' : 'border-border text-foreground'}
                data-testid="dark-mode-button"
              >
                <i className="fas fa-moon text-sm mr-1"></i>
                Escuro
              </Button>
            </div>
          </div>
        </div>

        {/* Botões de Ação */}
        <div className="flex gap-3 pt-4 border-t border-border">
          <Button
            onClick={resetSettings}
            variant="outline"
            className="flex-1 border-border text-foreground hover:bg-muted"
          >
            <i className="fas fa-undo mr-2"></i>
            Resetar
          </Button>

          <Button
            onClick={() => {
              const settingsToSave = {
                VOZ_MASC_ID: settings.mascVoiceId,
                VOZ_FEM_ID: settings.femVoiceId,
                IDIOMA: settings.language,
                VOLUME: settings.volume.toString(),
                AUTO_RECORD: settings.autoRecord.toString(),
                NOTIFICATIONS: settings.notifications.toString()
              };
              updateSettingsMutation.mutate(settingsToSave);
            }}
            className="flex-1 bg-abmix-green text-black hover:bg-abmix-green/90"
            disabled={updateSettingsMutation.isPending}
          >
            <i className="fas fa-save mr-2"></i>
            {updateSettingsMutation.isPending ? 'Salvando...' : 'Salvar'}
          </Button>
        </div>
      </div>
    </div>
  );
}

